CREATE OR REPLACE PROCEDURE pls_pp_lote_retencao_pck.valida_se_pode_des_fechar_lote ( nr_seq_lote_p pls_pp_lr_lote.nr_sequencia%type) AS $body$
DECLARE


cd_estabelecimento_w	pls_pp_lr_lote.cd_estabelecimento%type;


BEGIN

-- tenta buscar os dados do lote de pagamento
select	max(a.cd_estabelecimento)
into STRICT	cd_estabelecimento_w
from	pls_pp_lr_lote a
where	a.nr_sequencia	= nr_seq_lote_p;

if (coalesce(cd_estabelecimento_w::text, '') = '') then
	
	-- Lote de pagamento de produção médica não existe
	CALL wheb_mensagem_pck.exibir_mensagem_abort(388121, 'NR_SEQ_LOTE_PP_P=' || nr_seq_lote_p);
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
