CREATE OR REPLACE PROCEDURE pls_pp_lote_retencao_pck.valida_se_pode_gerar_lote ( nr_seq_lote_p pls_pp_lr_lote.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
DECLARE


ds_retorno_w	varchar(250);


BEGIN
-- retorna os lotes de pagamento que estão em aberto na competência
ds_retorno_w := pls_pp_lote_retencao_pck.obter_lotes_pgto_abertos_comp(current_setting('pls_pp_lote_retencao_pck.dt_mes_competencia_lote_w')::pls_pp_lr_lote.dt_mes_competencia%type);

-- se retornou  algum não pode ser gerado o lote de fechamento
if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') then

	-- Não é possível gerar o lote de fechamento, o(s) lote(s) estão em aberto:
	-- #@DS_MENSAGEM_P#@
	CALL wheb_mensagem_pck.exibir_mensagem_abort(460113, 'DS_MENSAGEM_P=' || ds_retorno_w);
end if;

END;

