CREATE OR REPLACE PROCEDURE pls_pp_nota_fiscal_pck.revincular_nota_prestador ( nr_seq_lote_p bigint, nr_seq_prestador_p bigint, cd_serie_nf_p text, cd_operacao_nf_p bigint, dt_emissao_p timestamp, cd_natureza_operacao_p bigint, ds_observacao_p text, ds_complemento_p text, dt_base_venc_p timestamp, nr_nota_fiscal_p text, nm_usuario_p text, cd_estabelecimento_p bigint, ie_base_venc_p text, nr_seq_nota_fiscal_p INOUT bigint) AS $body$
DECLARE


nr_nota_fiscal_w		varchar(255);
nr_lote_contabil_w		bigint;
nr_seq_nota_fiscal_w		bigint;


BEGIN
select	max(nr_nota_fiscal)
into STRICT	nr_nota_fiscal_w
from	pls_pp_prestador
where	nr_seq_lote		= nr_seq_lote_p
and	nr_seq_prestador	= nr_seq_prestador_p;

select	max(nr_lote_contabil),
	max(nr_sequencia)
into STRICT	nr_lote_contabil_w,
	nr_seq_nota_fiscal_w
from	nota_fiscal
where	nr_nota_fiscal		= nr_nota_fiscal_w;
--and	nr_seq_pgto_prest	= nr_seq_prestador_pgto_p;
if (nr_lote_contabil_w IS NOT NULL AND nr_lote_contabil_w::text <> '') and (nr_lote_contabil_w <> 0) then
	--Mensagem: A nota fiscal vinculada j√° foi contabilizada.
	CALL wheb_mensagem_pck.exibir_mensagem_abort(265320,'');
else
	CALL estornar_nota_fiscal(nr_seq_nota_fiscal_w, nm_usuario_p);
	
	/*update	nota_fiscal
	set	nr_seq_pgto_prest	= null
	where	nr_seq_pgto_prest	= nr_seq_prestador_pgto_p;*/
	
	update 	pls_pp_prestador
	set	nr_nota_fiscal		 = NULL
	where	nr_seq_lote		= nr_seq_lote_p
	and	nr_seq_prestador	= nr_seq_prestador_p;
	
	nr_seq_nota_fiscal_p := pls_pp_nota_fiscal_pck.vincular_nota_lote_prod(nr_seq_lote_p, nr_seq_prestador_p, cd_serie_nf_p, cd_operacao_nf_p, dt_emissao_p, cd_natureza_operacao_p, ds_observacao_p, ds_complemento_p, dt_base_venc_p, nr_nota_fiscal_p, nm_usuario_p, cd_estabelecimento_p, ie_base_venc_p, nr_seq_nota_fiscal_p);
end if;

commit;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
