CREATE OR REPLACE PROCEDURE pls_pp_tit_pag_rec_pck.insere_adiantamento_item ( nr_contador_p INOUT integer, tb_nr_adiantamento_p INOUT pls_util_cta_pck.t_number_table, tb_vl_saldo_p INOUT pls_util_cta_pck.t_number_table, tb_nr_seq_tipo_p INOUT pls_util_cta_pck.t_number_table, tb_nm_usuario_p INOUT pls_util_cta_pck.t_varchar2_table_15, tb_nr_seq_lote_p INOUT pls_util_cta_pck.t_number_table, tb_nr_seq_evento_p INOUT pls_util_cta_pck.t_number_table, tb_nr_seq_prestador_p INOUT pls_util_cta_pck.t_number_table, tb_nr_seq_regra_p INOUT pls_util_cta_pck.t_number_table, tb_tb_nr_seq_nota_p INOUT pls_util_cta_pck.t_number_table, tb_cd_centro_custo_p INOUT pls_util_cta_pck.t_number_table) AS $body$
DECLARE


cd_moeda_w		adiantamento_pago.cd_moeda%type;
nr_seq_adiant_dev_w	adiant_pago_dev.nr_sequencia%type;

BEGIN

if (tb_nr_adiantamento_p.count > 0) then
	-- insere o item no lote de pagamento
	forall i in tb_nr_adiantamento_p.first..tb_nr_adiantamento_p.last
		insert into pls_pp_item_lote(	
			nr_sequencia, dt_atualizacao, dt_atualizacao_nrec,
			ie_tipo_item, nm_usuario, nm_usuario_nrec,
			nr_adiantamento, nr_seq_lote, nr_seq_evento,
			nr_seq_prestador, nr_seq_regra_adiant, nr_seq_nota_fiscal,
			cd_centro_custo, vl_glosa, vl_item,
			vl_liquido, ie_acao_negativo, vl_acao_negativo,
			vl_desconto_tributo, ie_cancelado, nr_seq_prestador_origem
		) values (
			nextval('pls_pp_item_lote_seq'), clock_timestamp(), clock_timestamp(),
			'3', tb_nm_usuario_p(i), tb_nm_usuario_p(i),
			tb_nr_adiantamento_p(i), tb_nr_seq_lote_p(i), tb_nr_seq_evento_p(i),
			tb_nr_seq_prestador_p(i), tb_nr_seq_regra_p(i), tb_tb_nr_seq_nota_p(i),
			tb_cd_centro_custo_p(i), 0, tb_vl_saldo_p(i),
			tb_vl_saldo_p(i), 'N', 0,
			0, 'N', tb_nr_seq_prestador_p(i)
		);
	commit;

	-- percorre todos os adiantamentos e insere o registro de 'devolução' pois já será cobrado no pagamento
	for i in tb_nr_adiantamento_p.first..tb_nr_adiantamento_p.last loop

		select	max(cd_moeda)
		into STRICT	cd_moeda_w
		from	adiantamento_pago
		where	nr_adiantamento = tb_nr_adiantamento_p(i);

		select	coalesce(max(nr_sequencia), 0) + 1
		into STRICT	nr_seq_adiant_dev_w
		from	adiant_pago_dev;
		
		insert into adiant_pago_dev(
			nr_adiantamento, nr_sequencia, dt_devolucao,
			vl_devolucao, cd_moeda, dt_atualizacao,
			nm_usuario, ds_motivo_dev
		) values (
			tb_nr_adiantamento_p(i), nr_seq_adiant_dev_w, clock_timestamp(),
			abs(tb_vl_saldo_p(i)), cd_moeda_w, clock_timestamp(),
			tb_nm_usuario_p(i), 'Devolução gerada através de regra de adiantamento pago (' || tb_nr_seq_regra_p(i) ||
						') do lote de pagamento de produção médica ' || tb_nr_seq_lote_p(i) || '.'
		);

		-- atualiza o saldo e coloca a data da baixa no adiantamento
		CALL atualizar_saldo_adiant_pago(tb_nr_adiantamento_p(i), tb_nm_usuario_p(i));

		commit;
	end loop;
end if;

nr_contador_p := 0;
tb_nr_adiantamento_p.delete;
tb_vl_saldo_p.delete;
tb_nr_seq_tipo_p.delete;
tb_nm_usuario_p.delete;
tb_nr_seq_lote_p.delete;
tb_nr_seq_evento_p.delete;
tb_nr_seq_prestador_p.delete;
tb_nr_seq_regra_p.delete;
tb_tb_nr_seq_nota_p.delete;
tb_cd_centro_custo_p.delete;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
