CREATE OR REPLACE FUNCTION pls_pp_tit_pag_rec_pck.obter_ordem_sug_adi_pag_regra ( nr_seq_regra_adiant_p pls_evento_regra_adiant.nr_sequencia%type) RETURNS bigint AS $body$
DECLARE


nr_valor_total_w	pls_evento_regra_adiant.nr_ordem_execucao%type;

C01 CURSOR(	nr_seq_regra_adiant_pc		pls_evento_regra_adiant.nr_sequencia%type) FOR
	SELECT	dt_inicio_vigencia,
		dt_fim_vigencia,
		cd_pessoa_fisica,
		cd_cgc,
		nr_seq_tipo_adiant
	from	pls_evento_regra_adiant
	where	nr_sequencia = nr_seq_regra_adiant_pc;
	
BEGIN
-- iniciliza a vari√°vel
nr_valor_total_w := 0;

-- verifica a regra e adiciona 500 pontos para cada campo preenchido
for r_C01_w in C01( nr_seq_regra_adiant_p ) loop
	-- campo dt_inicio_vigencia
	if (r_C01_w.dt_inicio_vigencia IS NOT NULL AND r_C01_w.dt_inicio_vigencia::text <> '') then
		nr_valor_total_w := nr_valor_total_w + 500;
	end if;
	
	-- campo dt_fim_vigencia
	if (r_C01_w.dt_fim_vigencia IS NOT NULL AND r_C01_w.dt_fim_vigencia::text <> '') then
		nr_valor_total_w := nr_valor_total_w + 500;
	end if;
	
	-- campo cd_pessoa_fisica
	if (r_C01_w.cd_pessoa_fisica IS NOT NULL AND r_C01_w.cd_pessoa_fisica::text <> '') then
		nr_valor_total_w := nr_valor_total_w + 500;
	end if;
	
	-- campo cd_cgc
	if (r_C01_w.cd_cgc IS NOT NULL AND r_C01_w.cd_cgc::text <> '') then
		nr_valor_total_w := nr_valor_total_w + 500;
	end if;
	
	-- campo nr_seq_tipo_adiant
	if (r_C01_w.nr_seq_tipo_adiant IS NOT NULL AND r_C01_w.nr_seq_tipo_adiant::text <> '') then
		nr_valor_total_w := nr_valor_total_w + 500;
	end if;
end loop;

return nr_valor_total_w;

END;

$body$
