-- function que retorna o max() pls_pp_prestador_tmp conforme título a pagar ou título a receber
CREATE OR REPLACE FUNCTION pls_pp_tit_pag_rec_pck.obter_prestador_tmp_pf_pj ( nr_seq_prestador_p pls_prestador.nr_sequencia%type, cd_pessoa_fisica_p titulo_pagar.cd_pessoa_fisica%type, cd_cgc_p titulo_pagar.cd_cgc%type) RETURNS bigint AS $body$
DECLARE

					
nr_seq_prestador_w		pls_prestador.nr_sequencia%type;


BEGIN

if (nr_seq_prestador_p IS NOT NULL AND nr_seq_prestador_p::text <> '') then
	nr_seq_prestador_w := nr_seq_prestador_p;
else
	if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	
		-- favorece os ativos
		select	max(a.nr_sequencia)
		into STRICT	nr_seq_prestador_w
		from	pls_pp_prestador_tmp a
		where	a.cd_pessoa_fisica = cd_pessoa_fisica_p
		and	a.ie_situacao = 'A';
		
		if (coalesce(nr_seq_prestador_w::text, '') = '') then
			select	max(a.nr_sequencia)
			into STRICT	nr_seq_prestador_w
			from	pls_pp_prestador_tmp a
			where	a.cd_pessoa_fisica = cd_pessoa_fisica_p;
		end if;		
		
	elsif (cd_cgc_p IS NOT NULL AND cd_cgc_p::text <> '') then
	
		-- favorece os ativos		
		select	max(a.nr_sequencia)
		into STRICT	nr_seq_prestador_w
		from	pls_pp_prestador_tmp a
		where	a.cd_cgc = cd_cgc_p
		and	a.ie_situacao = 'A';
		
		if (coalesce(nr_seq_prestador_w::text, '') = '') then
			select	max(a.nr_sequencia)
			into STRICT	nr_seq_prestador_w
			from	pls_pp_prestador_tmp a
			where	a.cd_cgc = cd_cgc_p;		
		end if;
	end if;
end if;

return nr_seq_prestador_w;

END;

$body$
