CREATE OR REPLACE FUNCTION pls_pp_tributacao_pck.calcula_vl_trib_inss ( ie_acumulativo_p tributo_conta_pagar.ie_acumulativo%type, cd_tributo_p tributo.cd_tributo%type, vl_teto_base_p tributo_conta_pagar.vl_teto_base_calculo%type, vl_minimo_base_p tributo_conta_pagar.vl_minimo%type, vl_minimo_tributo_p tributo_conta_pagar.vl_minimo_tributo%type, vl_tributo_pago_p pls_pp_valor_trib_pessoa.vl_tributo_pago%type, vl_base_acumulada_p pls_pp_valor_trib_pessoa.vl_base_acumulada%type, dt_competencia_p timestamp, vl_base_total_p pls_pp_valor_trib_pessoa.vl_base_total%type, pr_aliquota_p INOUT tributo_conta_pagar.pr_aliquota%type, vl_base_calculo_p INOUT pls_pp_valor_trib_pessoa.vl_base_calculo%type, vl_trib_nao_retido_p INOUT pls_pp_valor_trib_pessoa.vl_nao_retido%type, vl_trib_adic_p INOUT pls_pp_valor_trib_pessoa.vl_trib_adic%type, vl_base_nao_retido_p INOUT pls_pp_valor_trib_pessoa.vl_base_nao_retido%type, vl_base_adic_p INOUT pls_pp_valor_trib_pessoa.vl_base_adic%type, vl_base_total_out_p out pls_pp_valor_trib_pessoa.vl_base_total%type, vl_base_acum_out_p out pls_pp_valor_trib_pessoa.vl_base_acumulada%type, vl_reducao_p out regra_calculo_irpf.vl_reducao%type, nr_seq_regra_irpf_p out regra_calculo_irpf.nr_sequencia%type) AS $body$
DECLARE


/* 	
	ESTA ROTINA POSSUI OS TRATAMENTOS PARA O LOTE DE PAGAMENTO, QUALQUER ALTERAcaO NESTA FAVOR VERIFICAR SE NaO e NECESSaRIO 
	FAZER O MESMO TRATAMENTO NA ROTINA UTILIZADA NOS LOTES DE FECHAMENTO DO MeS (PLS_PP_LOTE_RETENCAO_PCK.CALCULA_VL_TRIB_INSS)

	vl_base_acumulada_p = foi decidido que iremos passar no vl_base_anterior_p a soma dos valores retidos em outra empresa e o valor acumulado
			pois caso passamos no vl_base_retido_outro_p esta base e retornada na base utilizada para o calculo do tributo mesmo que
			nao tenha sido utilizada 
*/


vl_minimo_tributo_w	tributo_conta_pagar.vl_minimo_tributo%type;
vl_tributo_w		pls_pp_valor_trib_pessoa.vl_tributo%type;
vl_irrelevante_w	double precision;
nr_seq_regra_irpf_w	regra_calculo_irpf.nr_sequencia%type;
vl_trib_nao_retido_w	pls_pp_valor_trib_pessoa.vl_nao_retido%type;
vl_trib_adic_w		pls_pp_valor_trib_pessoa.vl_trib_adic%type;
vl_base_nao_retido_w	pls_pp_valor_trib_pessoa.vl_base_nao_retido%type;
vl_base_adic_w		pls_pp_valor_trib_pessoa.vl_base_adic%type;
vl_base_atual_w		pls_pp_valor_trib_pessoa.vl_base_atual%type := vl_base_calculo_p;


BEGIN
-- inicializa a variavel

vl_irrelevante_w := 0;

-- caso o campo esteja nulo ou venha zero, entao assume 0.01, isto pois existe um tratamento na obter_valores_tributo

-- que so ira gerar tributo da diferenca entre o valor retido em outras empresas e o valor atual quando esse valor for maior que zero

if (coalesce(vl_minimo_tributo_p, 0) = 0) then

	vl_minimo_tributo_w := 0.01;
else
	vl_minimo_tributo_w := vl_minimo_tributo_p;
end if;

obter_valores_tributo(	ie_acumulativo_p, -- vem do cadastro do tributo indicando se o mesmo e acumulativo
			pr_aliquota_p, -- in out entra a aliquota atual sai a nova (utilizado para o IRPF quando troca de aliquota por algum motivo)
			vl_minimo_base_p,
			vl_minimo_tributo_w, -- tratado para que seja no minimo 0.01 pois existe tratamento dentro da rotina
			vl_trib_nao_retido_p, -- vl_soma_trib_nao_retido_p nos testes feitos nao encontrei utilidade para INSS porem decidimos manter para testes
			vl_trib_adic_p, -- vl_soma_trib_adic_p este campo fica salvo os valores de tributo que nao foram 'pagos' em um outro processo por nao ter atingido o valor minimo
			vl_base_nao_retido_p, -- vl_soma_base_nao_retido_p nos testes feitos nao encontrei utilidade para INSS porem decidimos manter para testes
			vl_base_adic_p, -- vl_soma_base_adic_p para IR este campo traz a informacao de base anterior, para INSS nao temos certeza porem mantemos o valor
			vl_base_calculo_p, -- in out entra o valor de base atual e sai o valor que foi utilizado para chegar no valor do tributo
			vl_tributo_w, -- valor do tributo calculado
			vl_trib_nao_retido_w, -- vl_trib_nao_retido_p salvamos estes dados apenas por causa da view dos tributos
			vl_trib_adic_w, -- vl_trib_adic_p salvamos estes dados apenas por causa da view dos tributos
			vl_base_nao_retido_w, -- vl_base_nao_retido_p salvamos estes dados apenas por causa da view dos tributos
			vl_base_adic_w, -- vl_base_adic_p salvamos estes dados apenas por causa da view dos tributos
			vl_teto_base_p, -- vl teto do tributo
			vl_tributo_pago_p, -- tributo pago no sistema para a competencia ate o momento
			'N', -- ie_irpf_p, como estamos calculando INSS entao e passado fixo
			vl_base_acumulada_p, -- ver comentario no inicio desta funcao
			vl_irrelevante_w, -- vl_reducao_p nos testes feitos nao encontrei utilidade para INSS
			vl_irrelevante_w, -- vl_desc_dependente_p nao existe desconto por dependente para INSS
			0, -- qt_dependente_p nao existe desconto por dependente para INSS
			0, -- vl_base_pago_p nos testes feitos nao encontrei utilidade para INSS
			0, -- vl_base_pago_adic_base_p nos testes feitos nao encontrei utilidade para INSS
			0, -- vl_base_retido_outro_p decidido que os valores retido em outro de INSS serao passados como base acumulado
			0, -- vl_outras_red_irpf_p nos testes feitos nao encontrei utilidade para INSS
			dt_competencia_p,
			nr_seq_regra_irpf_w, -- out sequencia da regra de IRPF
			cd_tributo_p);

vl_trib_nao_retido_p := vl_trib_nao_retido_w;
vl_trib_adic_p := vl_trib_adic_w;
vl_base_nao_retido_p := vl_base_nao_retido_w;
vl_base_adic_p := vl_base_adic_w;

-- estes campos foram criados pois existe diferenca nos valores salvos nos mesmos dependendo do tipo de tributo

-- para INSS o valor salvo na base total sempre sera a base atual + base acumulada ja na base acumulada sempre sera a propria

vl_base_total_out_p := vl_base_total_p;
vl_base_acum_out_p := vl_base_acumulada_p;

-- estes campos sao especificos para IRPF, aqui apenas alimentamos para salvar na tabela

vl_reducao_p := 0;
nr_seq_regra_irpf_p := null;

-- O tributo de INSS nao pode ser gerado com valor negativo, impacta direto no IR

-- Desta forma zera o valor e joga pro proximo pagamento

if (vl_tributo_w < 0) then
	vl_tributo_w := 0;
end if;

-- Se a base de calculo for menor que zero, nao gera dados de tributo, por que vai gerar evento negativo de apropriacao

if (vl_base_atual_w < 0) then
	vl_trib_nao_retido_p	:= 0;
	vl_trib_adic_p		:= 0;
	vl_base_nao_retido_p	:= 0;
	vl_base_adic_p		:= 0;
	vl_base_calculo_p	:= 0;
	vl_base_acum_out_p	:= 0;
	vl_base_total_out_p	:= 0;
	vl_tributo_w		:= 0;
	vl_tributo_w 		:= 0;
	nr_seq_regra_irpf_p	:= nr_seq_regra_irpf_w;
end if;

return;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON FUNCTION pls_pp_tributacao_pck.calcula_vl_trib_inss ( ie_acumulativo_p tributo_conta_pagar.ie_acumulativo%type, cd_tributo_p tributo.cd_tributo%type, vl_teto_base_p tributo_conta_pagar.vl_teto_base_calculo%type, vl_minimo_base_p tributo_conta_pagar.vl_minimo%type, vl_minimo_tributo_p tributo_conta_pagar.vl_minimo_tributo%type, vl_tributo_pago_p pls_pp_valor_trib_pessoa.vl_tributo_pago%type, vl_base_acumulada_p pls_pp_valor_trib_pessoa.vl_base_acumulada%type, dt_competencia_p timestamp, vl_base_total_p pls_pp_valor_trib_pessoa.vl_base_total%type, pr_aliquota_p INOUT tributo_conta_pagar.pr_aliquota%type, vl_base_calculo_p INOUT pls_pp_valor_trib_pessoa.vl_base_calculo%type, vl_trib_nao_retido_p INOUT pls_pp_valor_trib_pessoa.vl_nao_retido%type, vl_trib_adic_p INOUT pls_pp_valor_trib_pessoa.vl_trib_adic%type, vl_base_nao_retido_p INOUT pls_pp_valor_trib_pessoa.vl_base_nao_retido%type, vl_base_adic_p INOUT pls_pp_valor_trib_pessoa.vl_base_adic%type, vl_base_total_out_p out pls_pp_valor_trib_pessoa.vl_base_total%type, vl_base_acum_out_p out pls_pp_valor_trib_pessoa.vl_base_acumulada%type, vl_reducao_p out regra_calculo_irpf.vl_reducao%type, nr_seq_regra_irpf_p out regra_calculo_irpf.nr_sequencia%type) FROM PUBLIC;
