SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_pp_tributacao_pck.desfazer_tributos_lote ( nr_seq_lote_p pls_pp_lote.nr_sequencia%type) AS $body$
DECLARE


tb_nr_sequencia_w	pls_util_cta_pck.t_number_table;

-- order by para limpar primeiro o que tem o autorelacionamento setado

c01 CURSOR(	nr_seq_lote_pc	pls_pp_lote.nr_sequencia%type) FOR
	SELECT	a.nr_sequencia nr_seq_trib_pessoa
	from	pls_pp_valor_trib_pessoa a
	where	a.nr_seq_lote = nr_seq_lote_pc
	order by nr_seq_vl_deduzir_ir;
	
c02 CURSOR(	nr_seq_lote_pc	pls_pp_lote.nr_sequencia%type) FOR
	SELECT	a.nr_sequencia
	from	pls_pp_base_acum_trib a
	where	a.nr_seq_lote_pgto_orig = nr_seq_lote_pc;

c03 CURSOR(	nr_seq_lote_pc	pls_pp_lote.nr_sequencia%type) FOR
	SELECT	a.nr_sequencia
	from	pls_pp_base_atual_trib a
	where	a.nr_seq_lote = nr_seq_lote_pc;

c04 CURSOR(	nr_seq_lote_pc	pls_pp_lote.nr_sequencia%type) FOR
	SELECT	a.nr_sequencia
	from	pls_pp_item_lote a
	where	a.nr_seq_lote = nr_seq_lote_pc;


BEGIN

-- primeiro e necessario limpar a tabela pls_pp_valor_trib_pessoa

open c01(nr_seq_lote_p);
loop
	fetch c01 bulk collect into	tb_nr_sequencia_w
	limit pls_util_pck.qt_registro_transacao_w;
	exit when tb_nr_sequencia_w.count = 0;
	
	-- limpa o vinculo da tabela pls_pp_valor_trib_pessoa com a pls_pp_base_atual_trib

	forall i in tb_nr_sequencia_w.first..tb_nr_sequencia_w.last
		update 	pls_pp_base_atual_trib
		set	nr_seq_trib_pessoa  = NULL
		where	nr_seq_lote = nr_seq_lote_p
		and	nr_seq_trib_pessoa = tb_nr_sequencia_w(i);
	commit;
	
	-- limpa o vinculo da tabela pls_pp_valor_trib_pessoa com a pls_pp_ba_acu_trib_pess

	forall i in tb_nr_sequencia_w.first..tb_nr_sequencia_w.last
		delete
		from	pls_pp_ba_acu_trib_pess
		where	nr_seq_trib_pessoa = tb_nr_sequencia_w(i);
	commit;
	
	-- exclui os registros

	forall i in tb_nr_sequencia_w.first..tb_nr_sequencia_w.last
		delete
		from	pls_pp_valor_trib_pessoa
		where	nr_sequencia = tb_nr_sequencia_w(i);
	commit;
end loop;
close c01;



open c02(nr_seq_lote_p);
loop
	fetch c02 bulk collect into	tb_nr_sequencia_w
	limit pls_util_pck.qt_registro_transacao_w;
	exit when tb_nr_sequencia_w.count = 0;

	-- exclui os registros

	forall i in tb_nr_sequencia_w.first..tb_nr_sequencia_w.last
		delete	
		from	pls_pp_ba_acu_trib_pess
		where	nr_seq_base_acumulada = tb_nr_sequencia_w(i);
	commit;
	
	-- exclui os registros

	forall i in tb_nr_sequencia_w.first..tb_nr_sequencia_w.last
		delete	
		from	pls_pp_base_acum_trib
		where	nr_seq_lote_pgto_orig = nr_seq_lote_p
		and	nr_sequencia = tb_nr_sequencia_w(i);
	commit;
end loop;
close c02;

	
open c03(nr_seq_lote_p);
loop
	fetch c03 bulk collect into	tb_nr_sequencia_w
	limit pls_util_pck.qt_registro_transacao_w;
	exit when tb_nr_sequencia_w.count = 0;
	
	-- exclui os registros

	forall i in tb_nr_sequencia_w.first..tb_nr_sequencia_w.last
		delete from pls_pp_base_atual_trib
		where nr_sequencia = tb_nr_sequencia_w(i);
	commit;
end loop;
close c03;

open c04(nr_seq_lote_p);
loop
	fetch c04 bulk collect into	tb_nr_sequencia_w
	limit pls_util_pck.qt_registro_transacao_w;
	exit when tb_nr_sequencia_w.count = 0;

	forall i in tb_nr_sequencia_w.first..tb_nr_sequencia_w.last
		update	pls_pp_item_lote
		set	vl_liquido = vl_liquido + coalesce(vl_desconto_tributo, 0),
			vl_desconto_tributo = 0
		where	nr_sequencia = tb_nr_sequencia_w(i);
	commit;
end loop;
close c04;

CALL pls_desfazer_log_trib_pagto(	null, nr_seq_lote_p);

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_pp_tributacao_pck.desfazer_tributos_lote ( nr_seq_lote_p pls_pp_lote.nr_sequencia%type) FROM PUBLIC;
