CREATE OR REPLACE PROCEDURE pls_pp_tributacao_pck.gerar_vl_base_tributacao ( nr_seq_lote_p pls_pp_lote.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


c01 CURSOR FOR
	SELECT	a.cd_tributo,
		a.ie_tipo_tributo,
		a.ie_vencimento,
		a.ie_apuracao_piso,
		a.ie_restringe_estab,
		a.ie_baixa_titulo,
		a.ie_corpo_item,
		a.ie_repasse_titulo,
		a.ie_pf_pj
	from   	pls_tributo_v a
	where	a.ie_situacao = 'A';

BEGIN

for r_c01_w in c01 loop

	-- percorre as regras do tributos e verifica se existem itens no pagamento que se encaixam na regra.

	CALL pls_pp_tributacao_pck.gerencia_regra_vl_base_trib(	nr_seq_lote_p, r_c01_w.cd_tributo, r_c01_w.ie_vencimento,
					r_c01_w.ie_pf_pj, nm_usuario_p);

	-- verifica os impostos que precisam ter sua base acumulada atualizados

	CALL pls_pp_tributacao_pck.gerencia_vl_base_acumulado(	nr_seq_lote_p, r_c01_w.cd_tributo, r_c01_w.ie_tipo_tributo,
					r_c01_w.ie_vencimento, r_c01_w.ie_apuracao_piso, r_c01_w.ie_restringe_estab,
					r_c01_w.ie_baixa_titulo, r_c01_w.ie_corpo_item, r_c01_w.ie_repasse_titulo,
					cd_estabelecimento_p, nm_usuario_p);
end loop;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
