CREATE OR REPLACE FUNCTION pls_pp_tributacao_pck.obter_prest_pessoa_trib ( nr_seq_lote_p pls_pp_lote.nr_sequencia%type, cd_pessoa_fisica_p pessoa_fisica.cd_pessoa_fisica%type) RETURNS bigint AS $body$
DECLARE


nr_seq_prestador_w	pls_prestador.nr_sequencia%type;


BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

	-- primeiro tenta pegar algum prestador que ja faca parte da base atual

	select	max(a.nr_seq_prestador)
	into STRICT	nr_seq_prestador_w
	from	pls_pp_base_atual_trib a,
		pls_pp_prestador_tmp b
	where	b.ie_tipo_prestador = 'PF'
	and	b.cd_pessoa_fisica = cd_pessoa_fisica_p
	and	a.nr_seq_prestador = b.nr_seq_prestador;

	-- se nao encontrou entao pega o com maior sequencia

	if (coalesce(nr_seq_prestador_w::text, '') = '') then

		select	max(nr_seq_prestador)
		into STRICT	nr_seq_prestador_w
		from	pls_pp_prestador_tmp
		where	ie_tipo_prestador = 'PF'
		and	cd_pessoa_fisica = cd_pessoa_fisica_p;
	end if;
end if;

return nr_seq_prestador_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
