SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION pls_pp_tributacao_pck.obter_prest_tributo_sem_regra ( nr_seq_lote_p pls_pp_lote.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(150);

c01 CURSOR(	nr_seq_lote_pc	pls_pp_lote.nr_sequencia%type) FOR
	SELECT	a.nr_seq_prestador,
		a.cd_tributo
	from	pls_pp_valor_trib_pessoa a
	where	a.nr_seq_lote = nr_seq_lote_pc
	and	coalesce(a.nr_seq_trib_conta_pagar::text, '') = ''
	and	a.vl_tributo != 0;

BEGIN
-- retorna todos os prestadores e tributos que ficaram sem regra de calculo

for r_c01_w in c01(nr_seq_lote_p) loop

	ds_retorno_w := pls_util_pck.concatena_string(	ds_retorno_w, 'Prestador:' || r_c01_w.nr_seq_prestador || ' tributo:' || r_c01_w.cd_tributo,
							'.' || pls_util_pck.enter_w, 150);
end loop;

return ds_retorno_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
