SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION pls_pp_tributacao_pck.obter_tit_pag_trib ( nr_seq_trib_pessoa_p pls_pp_valor_trib_pessoa.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255) := null;

BEGIN
-- se nao foi passado a sequencia nem faz nada

if (nr_seq_trib_pessoa_p IS NOT NULL AND nr_seq_trib_pessoa_p::text <> '') then
	-- verifica se gerou por lote de retencao

	select	max(z.nr_titulo_pagar)
	into STRICT	ds_retorno_w
	from	pls_pp_valor_trib_pessoa x,
		pls_pp_lr_trib_pessoa z
	where	z.nr_sequencia	= x.nr_seq_lr_trib_pessoa
	and	z.cd_tributo	= x.cd_tributo
	and	x.nr_sequencia	= nr_seq_trib_pessoa_p;
	
	-- para os titulos de tributos que nao sao gerados pelo lote de retencao.

	if (coalesce(ds_retorno_w::text, '') = '') then
		select 	max(a.nr_titulo_pagar)
		into STRICT	ds_retorno_w
		from    pls_pp_valor_trib_pessoa a
		where	a.nr_sequencia = nr_seq_trib_pessoa_p;
	end if;
	
	if (coalesce(ds_retorno_w::text, '') = '') then
		select	max(x.nr_titulo)
		into STRICT	ds_retorno_w
		from	pls_pp_valor_trib_pessoa a,
			pls_pp_prestador p,
			titulo_pagar t,
			titulo_pagar_imposto i,
			titulo_pagar x
		where	p.nr_seq_prestador	= a.nr_seq_prestador
		and	p.nr_seq_lote		= a.nr_seq_lote
		and	t.nr_titulo		= p.nr_titulo_pagar
		and	t.nr_titulo		= i.nr_titulo
		and	i.cd_tributo		= a.cd_tributo
		and	x.nr_seq_tributo	= i.nr_sequencia
		and	a.nr_sequencia		= nr_seq_trib_pessoa_p;
	end if;
end if;

return ds_retorno_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
