CREATE OR REPLACE PROCEDURE pls_preco_beneficiario_pck.atualizar_desconto_benef (nr_seq_contrato_p pls_contrato.nr_sequencia%type, dt_preco_p pls_segurado_preco.dt_reajuste%type, ds_observacao_p pls_segurado_preco.ds_observacao%type, ie_commit_p text, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
DECLARE


qt_regra_desc_w		bigint;
nr_seq_seg_preco_w	pls_segurado_preco.nr_sequencia%type;

C01 CURSOR FOR
	SELECT	nr_sequencia nr_seq_segurado
	from	pls_segurado
	where	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
	and	((coalesce(dt_rescisao::text, '') = '') or (dt_rescisao > dt_preco_p))
	and	coalesce(dt_cancelamento::text, '') = ''
	and	nr_seq_contrato	= nr_seq_contrato_p;

BEGIN

select	count(1)
into STRICT	qt_regra_desc_w
from	pls_regra_desconto a,
	pls_preco_regra b
where	a.nr_sequencia	= b.nr_seq_regra
and	a.ie_situacao	= 'A'
and	dt_preco_p between coalesce(a.dt_inicio_vigencia,dt_preco_p) and coalesce(a.dt_fim_vigencia,dt_preco_p)
and	a.nr_seq_contrato = nr_seq_contrato_p;

if (qt_regra_desc_w > 0) then
	for r_c01_w in c01 loop
		begin
		nr_seq_seg_preco_w := pls_preco_beneficiario_pck.inserir_preco_benef(	r_c01_w.nr_seq_segurado, 'M', 'S', dt_preco_p, ie_commit_p, ds_observacao_p, null, null, null, nm_usuario_p, cd_estabelecimento_p, null, null, null, null, nr_seq_seg_preco_w);
		end;
	end loop;
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
