CREATE OR REPLACE PROCEDURE pls_preco_beneficiario_pck.atualizar_faixa_etaria_benef (nr_seq_segurado_p pls_segurado.nr_sequencia%type, dt_preco_p pls_segurado_preco.dt_reajuste%type, dt_referencia_mens_p pls_mensalidade_segurado.dt_mesano_referencia%type, nr_seq_regra_susp_reaj_fx_p pls_susp_reaj_fx_etaria.nr_sequencia%type, nr_seq_lote_mens_p pls_lote_mensalidade.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nr_seq_seg_preco_p INOUT pls_segurado_preco.nr_sequencia%type) AS $body$
DECLARE


dt_preco_w			pls_segurado_preco.dt_reajuste%type;
nr_seq_seg_preco_w		pls_segurado_preco.nr_sequencia%type;
nr_seq_preco_susp_fx_etaria_w	pls_segurado_preco.nr_sequencia%type;


BEGIN
dt_preco_w	:= null;

select	max(nr_sequencia)
into STRICT	nr_seq_preco_susp_fx_etaria_w
from	pls_segurado_preco
where	nr_seq_segurado	= nr_seq_segurado_p
and	ie_situacao	= 'A'
and	dt_reajuste	>= trunc(dt_preco_p,'mm')
and	cd_motivo_reajuste = 'SF';

if (coalesce(nr_seq_preco_susp_fx_etaria_w::text, '') = '') then
	select	max(nr_sequencia)
	into STRICT	nr_seq_preco_susp_fx_etaria_w
	from	pls_segurado_preco
	where	nr_seq_segurado	= nr_seq_segurado_p
	and	trunc(dt_reajuste,'mm')	= trunc(dt_preco_p,'mm')
	and	cd_motivo_reajuste = 'E';
end if;

if	(nr_seq_preco_susp_fx_etaria_w IS NOT NULL AND nr_seq_preco_susp_fx_etaria_w::text <> '' AND nr_seq_regra_susp_reaj_fx_p IS NOT NULL AND nr_seq_regra_susp_reaj_fx_p::text <> '') then
	dt_preco_w	:= fim_mes(add_months(dt_referencia_mens_p,-1));
elsif (coalesce(nr_seq_regra_susp_reaj_fx_p::text, '') = '') and (nr_seq_preco_susp_fx_etaria_w IS NOT NULL AND nr_seq_preco_susp_fx_etaria_w::text <> '') then
	select	max(nr_sequencia)
	into STRICT	nr_seq_preco_susp_fx_etaria_w
	from	pls_segurado_preco
	where	nr_seq_segurado	= nr_seq_segurado_p
	and	trunc(dt_reajuste,'mm')	between trunc(add_months(dt_referencia_mens_p,-2),'mm') and trunc(add_months(dt_referencia_mens_p,-1),'mm')
	and	cd_motivo_reajuste = 'E'
	and	(nr_seq_regra_susp_reaj_fx IS NOT NULL AND nr_seq_regra_susp_reaj_fx::text <> '');

	if (nr_seq_preco_susp_fx_etaria_w IS NOT NULL AND nr_seq_preco_susp_fx_etaria_w::text <> '') then
		dt_preco_w	:= fim_mes(add_months(dt_referencia_mens_p,-1));
	end if;
end if;

if (coalesce(dt_preco_w::text, '') = '') then
	dt_preco_w	:= fim_mes(dt_preco_p);
end if;

nr_seq_seg_preco_w := pls_preco_beneficiario_pck.inserir_preco_benef(	nr_seq_segurado_p, 'E', 'S', dt_preco_w, 'N', null, dt_referencia_mens_p, null, null, nm_usuario_p, cd_estabelecimento_p, null, nr_seq_regra_susp_reaj_fx_p, nr_seq_lote_mens_p, null, nr_seq_seg_preco_w);

nr_seq_seg_preco_p	:= nr_seq_seg_preco_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
