CREATE OR REPLACE PROCEDURE pls_preco_beneficiario_pck.gravar_reajuste_programado ( dt_preco_p pls_segurado_preco.dt_reajuste%type, nr_seq_plano_preco_p pls_plano_preco.nr_sequencia%type, nr_seq_segurado_p pls_segurado.nr_sequencia%type, nr_seq_regra_susp_reaj_fx_p pls_susp_reaj_fx_etaria.nr_sequencia%type, nr_seq_lote_mens_p pls_lote_mensalidade.nr_sequencia%type, ie_commit_p text, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
DECLARE


nr_seq_seg_preco_w	pls_segurado_preco.nr_sequencia%type;
ie_gerar_preco_ant_w	varchar(1);
dt_lote_w		pls_lote_mensalidade.dt_mesano_referencia%type;

C01 CURSOR FOR
	SELECT	a.dt_reajuste,
		a.nr_seq_reajuste
	from	pls_reajuste_preco a
	where	a.nr_seq_preco	= nr_seq_plano_preco_p
	and	a.dt_reajuste	> dt_preco_p
	and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
	and	not exists (	SELECT	1
				from	pls_reajuste_preco x
				where	a.nr_seq_preco	= x.nr_seq_preco
				and	a.dt_reajuste	= x.dt_reajuste
				and	x.nr_sequencia	> a.nr_sequencia
				and	(x.dt_liberacao IS NOT NULL AND x.dt_liberacao::text <> ''));
				
BEGIN

if (nr_seq_regra_susp_reaj_fx_p IS NOT NULL AND nr_seq_regra_susp_reaj_fx_p::text <> '') then
	select	max(dt_mesano_referencia)
	into STRICT	dt_lote_w
	from	pls_lote_mensalidade
	where	nr_sequencia = nr_seq_lote_mens_p;
	
	if (dt_preco_p <> dt_lote_w) then
		ie_gerar_preco_ant_w	:= 'N';
	else
		ie_gerar_preco_ant_w	:= 'S';
	end if;
else
	ie_gerar_preco_ant_w	:= 'N';
end if;

for r_c01_w in C01 loop
	begin
	nr_seq_seg_preco_w := pls_preco_beneficiario_pck.inserir_preco_benef(	nr_seq_segurado_p, 'I', 'S', r_c01_w.dt_reajuste, ie_commit_p, null, null, r_c01_w.nr_seq_reajuste, null, nm_usuario_p, cd_estabelecimento_p, null, null, null, ie_gerar_preco_ant_w, nr_seq_seg_preco_w);
	end;
end loop;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
