CREATE OR REPLACE PROCEDURE pls_preco_beneficiario_pck.inativar_reajuste_futuro ( nr_seq_segurado_p pls_segurado.nr_sequencia%type, dt_preco_p pls_segurado_preco.dt_reajuste%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
BEGIN

update	pls_segurado_preco
set	ie_situacao	= 'I',
	ds_observacao	= substr(CASE WHEN ds_observacao = NULL THEN null  ELSE ' - ' END  ||ds_observacao || 'Registro inativado.',1,255),
	nm_usuario	= nm_usuario_p,
	dt_atualizacao	= clock_timestamp()
where	nr_seq_segurado = nr_seq_segurado_p
and	ie_situacao	= 'A'
and	dt_reajuste	> dt_preco_p
and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');

END;

$body$
