CREATE OR REPLACE PROCEDURE pls_preco_beneficiario_pck.reajustar_alteracao_produto ( dt_preco_p pls_segurado_preco.dt_reajuste%type, nr_seq_segurado_p pls_segurado.nr_sequencia%type, nr_seq_regra_susp_reaj_fx_p pls_susp_reaj_fx_etaria.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
DECLARE


nr_seq_seg_preco_w	pls_segurado_preco.nr_sequencia%type;
ie_gerar_preco_ant_w	varchar(1);

c01 CURSOR FOR
	SELECT	dt_reajuste
	from	pls_segurado_preco
	where	nr_seq_segurado = nr_seq_segurado_p
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
	and	dt_reajuste > dt_preco_p
	and	cd_motivo_reajuste = 'P';
	
BEGIN

if (nr_seq_regra_susp_reaj_fx_p IS NOT NULL AND nr_seq_regra_susp_reaj_fx_p::text <> '') then
	ie_gerar_preco_ant_w	:= 'S';
else
	ie_gerar_preco_ant_w	:= 'N';
end if;

for r_c01_w in C01 loop
	begin
	nr_seq_seg_preco_w := pls_preco_beneficiario_pck.inserir_preco_benef(	nr_seq_segurado_p, 'P', 'S', r_c01_w.dt_reajuste, 'S', null, null, null, null, nm_usuario_p, cd_estabelecimento_p, null, null, null, ie_gerar_preco_ant_w, nr_seq_seg_preco_w);
	end;
end loop;

END;

