CREATE OR REPLACE FUNCTION pls_preco_beneficiario_pck.verificar_alt_dt_nascimento (nr_seq_segurado_p pls_segurado.nr_sequencia%type, cd_pessoa_fisica_p pessoa_fisica.cd_pessoa_fisica%type) RETURNS varchar AS $body$
DECLARE


ie_alterou_data_nascimento_w	varchar(1);
qt_alteracao_dt_nascimento_w	bigint;
dt_atualizacao_preco_w		pls_segurado_preco.dt_atualizacao%type;
dt_nascimento_w			pessoa_fisica.dt_nascimento%type;


BEGIN

ie_alterou_data_nascimento_w	:= 'N';

if	(nr_seq_segurado_p IS NOT NULL AND nr_seq_segurado_p::text <> '' AND cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	select	max(dt_atualizacao)
	into STRICT	dt_atualizacao_preco_w
	from   	pls_segurado_preco
	where  	nr_seq_segurado = nr_seq_segurado_p
	and    	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
	and    	ie_situacao = 'A';

	select	max(dt_nascimento)
	into STRICT	dt_nascimento_w
	from	pessoa_fisica
	where	cd_pessoa_fisica = cd_pessoa_fisica_p;
	
	select	count(1)
	into STRICT	qt_alteracao_dt_nascimento_w
	from	pessoa_fisica_alteracao
	where	cd_pessoa_fisica = cd_pessoa_fisica_p
	and	dt_nascimento	<> dt_nascimento_w
	and	dt_atualizacao > dt_atualizacao_preco_w;
	
	if (qt_alteracao_dt_nascimento_w > 0) then
		ie_alterou_data_nascimento_w	:= 'S';
	end if;
end if;

return ie_alterou_data_nascimento_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
