CREATE OR REPLACE FUNCTION pls_preco_beneficiario_pck.verificar_se_recalcula_benef ( ie_calculo_vidas_p pls_tabela_preco.ie_calculo_vidas%type, nr_seq_titular_p pls_segurado.nr_seq_titular%type, nr_seq_segurado_p pls_segurado.nr_sequencia%type, nr_seq_titular_cursor_p pls_segurado.nr_seq_titular%type, nr_seq_segurado_cursor_p pls_segurado.nr_sequencia%type, nr_seq_parentesco_p grau_parentesco.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


qt_benef_w		bigint;


BEGIN

if (ie_calculo_vidas_p = 'A') then -- Todos
	return 'S';
elsif (ie_calculo_vidas_p = 'F') then -- Todos da familia
	if	((nr_seq_titular_p IS NOT NULL AND nr_seq_titular_p::text <> '') and
		((nr_seq_titular_p = nr_seq_titular_cursor_p) or (nr_seq_titular_p = nr_seq_segurado_cursor_p))) or
		((coalesce(nr_seq_titular_p::text, '') = '') and (nr_seq_segurado_p = nr_seq_titular_cursor_p)) then
		return 'S';
	end if;
elsif (ie_calculo_vidas_p = 'T') then -- Somente titulares
	if (coalesce(nr_seq_titular_p::text, '') = '') then
		return 'S';
	end if;
elsif (ie_calculo_vidas_p = 'D') then -- Somente dependentes
	if (nr_seq_titular_p IS NOT NULL AND nr_seq_titular_p::text <> '') then
		return 'S';
	end if;
elsif (ie_calculo_vidas_p = 'TD') then -- Titulares + Dependentes legais
	if (nr_seq_titular_p IS NOT NULL AND nr_seq_titular_p::text <> '') then
		select	count(1)
		into STRICT	qt_benef_w
		from	grau_parentesco x
		where	x.nr_sequencia = nr_seq_parentesco_p
		and	x.ie_tipo_parentesco = '1';
	else
		qt_benef_w := 0;
	end if;
	
	if	((coalesce(nr_seq_titular_p::text, '') = '') or (qt_benef_w > 0)) then
		return 'S';
	end if;
end if;

return 'N';

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
