CREATE OR REPLACE PROCEDURE pls_programacao_reajuste_pck.alterar_status_program_reaj ( nr_seq_programacao_p pls_prog_reaj_coletivo.nr_sequencia%type, ie_status_p text, ie_opcao_p text, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
DECLARE


/*
ie_opcao_p
AN	Aprovar negociacao
FN	Finalizar negociacao
RN	Rejeitar negociacao
DL	Desfazer liberacao
*/



BEGIN

update	pls_prog_reaj_coletivo
set	ie_status		= ie_status_p,
	dt_liberacao		= CASE WHEN ie_status_p='L' THEN  clock_timestamp()  ELSE null END ,
	nm_usuario		= nm_usuario_p,
	dt_atualizacao		= clock_timestamp(),
	dt_aprovacao		= CASE WHEN ie_opcao_p='AN' THEN  clock_timestamp()  ELSE null END ,
	nm_usuario_aprovacao	= CASE WHEN ie_opcao_p='AN' THEN  nm_usuario_p  ELSE null END
where	nr_sequencia	= nr_seq_programacao_p;

if (ie_status_p = 'L') then
	CALL pls_programacao_reajuste_pck.gravar_historico_sistema(nr_seq_programacao_p,wheb_mensagem_pck.get_texto(1127819),nm_usuario_p,cd_estabelecimento_p);
elsif (ie_status_p = 'P') then
	CALL pls_programacao_reajuste_pck.gravar_historico_sistema(nr_seq_programacao_p,wheb_mensagem_pck.get_texto(1127853),nm_usuario_p,cd_estabelecimento_p);
elsif (ie_status_p = 'N') then
	if (ie_opcao_p = 'DL') then
		CALL pls_programacao_reajuste_pck.gravar_historico_sistema(nr_seq_programacao_p,wheb_mensagem_pck.get_texto(1127824),nm_usuario_p,cd_estabelecimento_p);
	else
		CALL pls_programacao_reajuste_pck.gravar_historico_sistema(nr_seq_programacao_p,wheb_mensagem_pck.get_texto(1127852),nm_usuario_p,cd_estabelecimento_p);
	end if;
end if;

commit;

END;

$body$
