CREATE OR REPLACE PROCEDURE pls_programacao_reajuste_pck.atualizar_taxa_reajuste_lote ( nr_seq_lote_referencia_p pls_reajuste.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


tx_reajuste_w	pls_reajuste.tx_reajuste%type;


BEGIN

begin
select 	tx_reajuste
into STRICT	tx_reajuste_w
from (SELECT	tx_reajuste
	from	pls_reajuste
	where	nr_seq_lote_referencia = nr_seq_lote_referencia_p
	group by
		tx_reajuste) alias0;
exception
when others then
	tx_reajuste_w	:= 0;
end;

if (tx_reajuste_w <> 0) then
	update	pls_reajuste
	set	nm_usuario = nm_usuario_p,
		tx_reajuste = tx_reajuste_w
	where	nr_sequencia = nr_seq_lote_referencia_p;
	
	commit;
end if;

END;

