CREATE OR REPLACE PROCEDURE pls_programacao_reajuste_pck.deletar_registros ( ie_descarregar_vetores_p text) AS $body$
BEGIN
if	((indice_delete_w >= pls_util_pck.qt_registro_transacao_w) or (ie_descarregar_vetores_p = 'S')) then
	if (tb_nr_seq_programacao_w.count > 0) then
		forall i in tb_nr_seq_programacao_w.first..tb_nr_seq_programacao_w.last
			delete	FROM pls_prog_reaj_coletivo
			where	nr_sequencia = tb_nr_seq_programacao_w(i);
		commit;
	end if;
	
	CALL pls_programacao_reajuste_pck.limpar_vetor_delete();
end if;
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
