CREATE OR REPLACE PROCEDURE pls_programacao_reajuste_pck.desfazer_lote ( nr_seq_lote_p pls_prog_reaj_colet_lote.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
DECLARE

tb_nr_seq_prog_w	pls_util_cta_pck.t_number_table;
C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_prog_reaj_coletivo
	where	nr_seq_lote	= nr_seq_lote_p;

BEGIN
open C01;
loop
	fetch C01 bulk collect into tb_nr_seq_prog_w limit 1000;
	exit when tb_nr_seq_prog_w.count = 0;
	
	forall i in tb_nr_seq_prog_w.first..tb_nr_seq_prog_w.last
		delete	FROM pls_prog_reaj_equipe
		where	nr_seq_programacao	= tb_nr_seq_prog_w(i);
	commit;
	
	forall i in tb_nr_seq_prog_w.first..tb_nr_seq_prog_w.last
		delete	FROM pls_prog_reaj_coletivo
		where	nr_sequencia	= tb_nr_seq_prog_w(i);
	commit;
end loop;
close C01;

update	pls_prog_reaj_colet_lote
set	ie_status	= 1,
	dt_atualizacao	= clock_timestamp(),
	nm_usuario	= nm_usuario_p,
	dt_geracao_lote	 = NULL
where	nr_sequencia	= nr_seq_lote_p;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
