CREATE OR REPLACE PROCEDURE pls_programacao_reajuste_pck.finalizar_negociacao ( nr_seq_programacao_p pls_prog_reaj_coletivo.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
DECLARE


nr_seq_regra_lib_neg_reaj_w	pls_regra_lib_neg_reaj.nr_sequencia%type	:= null;
tx_reajuste_proposto_w		pls_prog_reaj_coletivo.tx_reajuste_proposto%type;
tx_reajuste_programado_w	pls_prog_reaj_coletivo.tx_reajuste_programado%type;
dt_reajuste_w			pls_prog_reaj_coletivo.dt_reajuste%type;
nr_seq_contrato_w		pls_contrato.nr_sequencia%type;
ie_percentual_reaj_w		pls_regra_lib_neg_reaj.ie_percentual_reaj_aplicado%type;


BEGIN

select	coalesce(tx_reajuste_proposto,0),
	coalesce(tx_reajuste_programado,0),
	dt_reajuste,
	nr_seq_contrato
into STRICT	tx_reajuste_proposto_w,
	tx_reajuste_programado_w,
	dt_reajuste_w,
	nr_seq_contrato_w
from	pls_prog_reaj_coletivo
where	nr_sequencia = nr_seq_programacao_p;

if (tx_reajuste_programado_w > tx_reajuste_proposto_w) then
	ie_percentual_reaj_w	:= '>';
elsif (tx_reajuste_programado_w < tx_reajuste_proposto_w) then
	ie_percentual_reaj_w	:= '<';
else
	ie_percentual_reaj_w	:= '=';
end if;

if (ie_percentual_reaj_w <> '=') then
	select	max(nr_sequencia)
	into STRICT	nr_seq_regra_lib_neg_reaj_w
	from	pls_regra_lib_neg_reaj
	where	ie_situacao = 'A'
	and	cd_estabelecimento = cd_estabelecimento_p
	and (ie_percentual_reaj_aplicado = ie_percentual_reaj_w or ie_percentual_reaj_aplicado = '<>');
end if;

if (nr_seq_regra_lib_neg_reaj_w IS NOT NULL AND nr_seq_regra_lib_neg_reaj_w::text <> '') then
	CALL pls_programacao_reajuste_pck.alterar_status_program_reaj(nr_seq_programacao_p, 'P', 'FN', nm_usuario_p, cd_estabelecimento_p);
else
	CALL pls_programacao_reajuste_pck.gerar_aporte(nr_seq_programacao_p, dt_reajuste_w, nr_seq_contrato_w, nm_usuario_p, cd_estabelecimento_p);
	CALL pls_programacao_reajuste_pck.alterar_status_program_reaj(nr_seq_programacao_p, 'L', 'FN', nm_usuario_p, cd_estabelecimento_p);
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
