CREATE OR REPLACE PROCEDURE pls_programacao_reajuste_pck.vincular_equipe ( nr_seq_programacao_p pls_prog_reaj_coletivo.nr_sequencia%type, nr_seq_equipe_p pls_equipe_reajuste.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
DECLARE

qt_pessoa_w	integer;
C01 CURSOR(	nr_seq_equipe_pc	pls_equipe_reajuste.nr_sequencia%type) FOR
	SELECT	cd_pessoa_fisica,
		nr_seq_equipe
	from	pls_equipe_reaj_pessoa
	where	nr_seq_equipe	= nr_seq_equipe_pc
	and	clock_timestamp() between trunc(coalesce(dt_inicio_vigencia,clock_timestamp()),'dd') and fim_dia(coalesce(dt_fim_vigencia,clock_timestamp()));
BEGIN
if (nr_seq_equipe_p IS NOT NULL AND nr_seq_equipe_p::text <> '') then
	for r_c01_w in C01(nr_seq_equipe_p) loop
		begin
		select	count(1)
		into STRICT	qt_pessoa_w
		from	pls_prog_reaj_equipe
		where	nr_seq_programacao	= nr_seq_programacao_p
		and	nr_seq_equipe		= r_c01_w.nr_seq_equipe
		and	cd_pessoa_fisica	= r_c01_w.cd_pessoa_fisica;
		
		if (qt_pessoa_w = 0) then
			insert	into	pls_prog_reaj_equipe(	nr_sequencia, dt_atualizacao_nrec, nm_usuario_nrec, dt_atualizacao, nm_usuario,
					nr_seq_programacao, nr_seq_equipe, cd_pessoa_fisica)
				values (	nextval('pls_prog_reaj_equipe_seq'), clock_timestamp(), nm_usuario_p, clock_timestamp(), nm_usuario_p,
					nr_seq_programacao_p, r_c01_w.nr_seq_equipe, r_c01_w.cd_pessoa_fisica);
		end if;
		end;
	end loop;
end if;
END;

$body$
