CREATE OR REPLACE PROCEDURE pls_reaj_cobranca_retro_pck.gerar_cobranca_coletivo ( nr_seq_reajuste_p pls_reajuste.nr_sequencia%type, dt_mensalidade_p pls_reajuste_cobr_retro.dt_referencia_mensalidade%type, dt_reajuste_p pls_reajuste_cobr_retro.dt_referencia_reajuste%type, qt_parcelas_p pls_reajuste_cobr_retro.qt_parcelas%type, ie_recomposicao_p pls_reajuste_cobr_retro.ie_recomposicao%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE

nr_seq_cobr_retro_w	pls_reajuste_cobr_retro.nr_sequencia%type;
C01 CURSOR FOR
	SELECT	a.nr_sequencia
	from	pls_reajuste a
	where	a.nr_seq_lote_referencia	= nr_seq_reajuste_p
	and	not exists (	SELECT	1
				from	pls_reajuste_cobr_retro x
				where	x.nr_seq_reajuste = a.nr_sequencia
				and	trunc(x.dt_referencia_reajuste,'month') = dt_reajuste_p);
BEGIN

for c01_w in C01 loop
	begin
	insert	into	pls_reajuste_cobr_retro(	nr_sequencia, nr_seq_reajuste, cd_estabelecimento,
			dt_atualizacao, nm_usuario, dt_atualizacao_nrec,
			nm_usuario_nrec, dt_referencia_mensalidade, dt_referencia_reajuste, 
			qt_parcelas, ie_recomposicao)
		values (	nextval('pls_reajuste_cobr_retro_seq'), c01_w.nr_sequencia, cd_estabelecimento_p,
			clock_timestamp(), nm_usuario_p, clock_timestamp(), 
			nm_usuario_p, trunc(dt_mensalidade_p,'month'), trunc(dt_reajuste_p,'month'), 
			qt_parcelas_p, ie_recomposicao_p)
			returning nr_sequencia into nr_seq_cobr_retro_w;
	
	CALL pls_reaj_cobranca_retro_pck.gerar_lancamentos(c01_w.nr_sequencia, nr_seq_cobr_retro_w, cd_estabelecimento_p, nm_usuario_p);
	end;
end loop;

commit;

END;

$body$
