CREATE OR REPLACE FUNCTION pls_reaj_cobranca_retro_pck.verificar_se_gera_cobr_retro ( dt_rescisao_p timestamp, dt_inicio_cobertura_p timestamp, nr_seq_mensalidade_seg_p pls_mensalidade_segurado.nr_sequencia%type, nr_seq_sca_vinculo_p pls_sca_vinculo.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


ie_gerar_cobr_retro_w	varchar(1);
			

BEGIN

ie_gerar_cobr_retro_w	:= 'S';

if (dt_rescisao_p < dt_inicio_cobertura_p) then
	ie_gerar_cobr_retro_w	:= 'N';
else
	if (nr_seq_sca_vinculo_p IS NOT NULL AND nr_seq_sca_vinculo_p::text <> '') then
		select	count(1)
		into STRICT	current_setting('pls_reaj_cobranca_retro_pck.qt_variacao_custo_w')::bigint
		from	pls_mensalidade_seg_item a,
			pls_mensalidade_sca b
		where	a.nr_seq_mensalidade_seg = nr_seq_mensalidade_seg_p
		and	a.nr_sequencia = b.nr_seq_item_mens
		and	b.nr_seq_vinculo_sca = nr_seq_sca_vinculo_p
		and	a.ie_tipo_item = '35';
	else
		select	count(1)
		into STRICT	current_setting('pls_reaj_cobranca_retro_pck.qt_variacao_custo_w')::bigint
		from	pls_mensalidade_seg_item
		where	nr_seq_mensalidade_seg = nr_seq_mensalidade_seg_p
		and	ie_tipo_item = '25';
	end if;
	
	if (current_setting('pls_reaj_cobranca_retro_pck.qt_variacao_custo_w')::bigint > 0) then
		ie_gerar_cobr_retro_w	:= 'N';
	end if;
end if;

return	ie_gerar_cobr_retro_w;

END;

$body$
