	current_setting('pls_reajuste_coletivo_pck.ie_mes_cobranca_reaj_w')::pls_parametros.ie_mes_cobranca_reaj%type,
	current_setting('pls_reajuste_coletivo_pck.ie_mes_cobranca_reaj_regulam_w')::pls_parametros.ie_mes_cobranca_reaj_reg%type,
	current_setting('pls_reajuste_coletivo_pck.ie_reajustar_benef_cancelado_w')::pls_parametros.ie_reajustar_benef_cancelado%type,
