CREATE OR REPLACE PROCEDURE pls_reajuste_coletivo_pck.inserir_tabela_orig_benef_int ( nr_seq_reajuste_p pls_reajuste.nr_sequencia%type, nr_seq_intercambio_p pls_reajuste.nr_seq_intercambio%type) AS $body$
DECLARE


C01 CURSOR(	nr_mes_reajuste_pc		bigint,
		dt_reajuste_pc			pls_reajuste.dt_reajuste%type,
		nr_seq_intercambio_pc		pls_reajuste.nr_seq_intercambio%type) FOR
	SELECT	b.nr_sequencia nr_seq_tabela,
		d.nr_sequencia nr_seq_plano,
		c.nr_seq_intercambio,
		c.dt_contrato,
		b.dt_inicio_vigencia,
		b.nr_segurado nr_seq_segurado,
		coalesce((pls_obter_dados_segurado(b.nr_sequencia, 'NRTI'))::numeric , b.nr_sequencia) nr_seq_titular,
		pls_obter_dados_segurado(b.nr_segurado, 'PF') cd_pessoa_fisica
	from	pls_segurado	a,
		pls_tabela_preco b,
		pls_contrato_temp c,
		pls_plano	d
	where	a.nr_seq_tabela		= b.nr_sequencia
	and	a.nr_seq_intercambio	= c.nr_seq_intercambio
	and	b.nr_seq_plano		= d.nr_sequencia
	and	d.ie_tipo_contratacao in ('CA', 'CE')
	and	c.nr_seq_intercambio	= nr_seq_intercambio_pc
	and	c.nr_mes_reajuste	= nr_mes_reajuste_pc;

BEGIN

for r_c01_w in c01(	(to_char(current_setting('pls_reajuste_coletivo_pck.pls_reajuste_w')::pls_reajuste%rowtype.dt_reajuste,'mm'))::numeric ,
			current_setting('pls_reajuste_coletivo_pck.pls_reajuste_w')::pls_reajuste%rowtype.dt_reajuste,
			nr_seq_intercambio_p) loop
	begin
	CALL pls_reajuste_coletivo_pck.alimentar_vetor_reaj_tabela(	r_c01_w.nr_seq_tabela, nr_seq_reajuste_p, null,
					r_c01_w.nr_seq_intercambio, r_c01_w.nr_seq_plano, r_c01_w.dt_contrato, 
					r_c01_w.nr_seq_segurado, r_c01_w.nr_seq_titular, r_c01_w.cd_pessoa_fisica);
	
	CALL CALL pls_reajuste_coletivo_pck.inserir_reajuste_tabela('N');
	end;
end loop; --C01

END;

$body$
