CREATE OR REPLACE PROCEDURE pls_reajuste_coletivo_pck.inserir_tabela_orig_benef ( nr_seq_reajuste_p pls_reajuste.nr_sequencia%type, nr_seq_contrato_p pls_reajuste.nr_seq_contrato%type) AS $body$
DECLARE


C01 CURSOR(	nr_seq_contrato_pc	pls_reajuste.nr_seq_contrato%type,
		nr_mes_reajuste_pc	bigint) FOR
	SELECT	c.nr_sequencia nr_seq_tabela,
		c.nr_seq_plano,
		b.nr_seq_contrato,
		b.dt_contrato,
		c.dt_inicio_vigencia,
		c.nr_segurado nr_seq_segurado,
		coalesce((pls_obter_dados_segurado(c.nr_segurado, 'NRTI'))::numeric , c.nr_segurado) nr_seq_titular,
		pls_obter_dados_segurado(c.nr_segurado, 'PF') cd_pessoa_fisica
	from	pls_segurado		a,
		pls_contrato_temp	b,
		pls_tabela_preco	c,
		pls_plano		d
	where	b.nr_seq_contrato	= a.nr_seq_contrato
	and	c.nr_sequencia		= a.nr_seq_tabela
	and	d.nr_sequencia		= c.nr_seq_plano
	and	b.nr_seq_contrato	= nr_seq_contrato_pc
	and	a.nr_mes_reajuste	= nr_mes_reajuste_pc
	and	not exists (	SELECT	1
				from	pls_contrato_plano x
				where	x.nr_seq_contrato	= b.nr_seq_contrato
				and	x.nr_seq_tabela		= a.nr_seq_tabela)
	
union

	select	c.nr_sequencia nr_seq_tabela,
		d.nr_sequencia nr_seq_plano,
		b.nr_seq_contrato,
		b.dt_contrato,
		c.dt_inicio_vigencia,
		c.nr_segurado nr_seq_segurado,
		coalesce((pls_obter_dados_segurado(c.nr_segurado, 'NRTI'))::numeric , c.nr_segurado) nr_seq_titular,
		pls_obter_dados_segurado(c.nr_segurado, 'PF') cd_pessoa_fisica
	from	pls_segurado		a,
		pls_contrato_temp	b,
		pls_tabela_preco	c,
		pls_plano		d
	where	b.nr_seq_contrato	= a.nr_seq_contrato
	and	d.nr_sequencia		= c.nr_seq_plano
	and	a.nr_sequencia		= c.nr_segurado
	and	a.nr_mes_reajuste	= nr_mes_reajuste_pc
	and	b.nr_seq_contrato	= nr_seq_contrato_pc
	and	((c.nr_contrato = a.nr_seq_contrato) or (coalesce(c.nr_contrato::text, '') = ''))
	and	not exists (	select	1
				from	pls_contrato_plano x
				where	x.nr_seq_contrato	= b.nr_seq_contrato
				and	x.nr_seq_tabela		= c.nr_sequencia)
	and	d.ie_tipo_operacao = 'B';

BEGIN

for r_c01_w in c01(nr_seq_contrato_p, (to_char(current_setting('pls_reajuste_coletivo_pck.pls_reajuste_w')::pls_reajuste%rowtype.dt_reajuste,'mm'))::numeric ) loop
	begin
	CALL pls_reajuste_coletivo_pck.alimentar_vetor_reaj_tabela(	r_c01_w.nr_seq_tabela, nr_seq_reajuste_p, r_c01_w.nr_seq_contrato,
					null, r_c01_w.nr_seq_plano, r_c01_w.dt_contrato, 
					r_c01_w.nr_seq_segurado, r_c01_w.nr_seq_titular, r_c01_w.cd_pessoa_fisica);
	CALL CALL pls_reajuste_coletivo_pck.inserir_reajuste_tabela('N');
	end;
end loop; --C01

END;

$body$
