CREATE OR REPLACE PROCEDURE pls_reajuste_coletivo_pck.inserir_tabela_sca_benef ( nr_seq_reajuste_p pls_reajuste.nr_sequencia%type, nr_seq_contrato_p pls_reajuste.nr_seq_contrato%type) AS $body$
DECLARE


C01 CURSOR(	nr_seq_contrato_pc	pls_reajuste.nr_seq_contrato%type,
		nr_mes_reajuste_pc	pls_contrato.nr_mes_reajuste%type,
		dt_reajuste_pc		pls_reajuste.dt_reajuste%type) FOR
	SELECT	d.nr_sequencia nr_seq_tabela,
		e.nr_sequencia nr_seq_plano_sca,
		a.nr_seq_contrato,
		a.dt_contrato,
		d.dt_inicio_vigencia,
		d.nr_segurado nr_seq_segurado,
		coalesce((pls_obter_dados_segurado(d.nr_segurado, 'NRTI'))::numeric , d.nr_segurado) nr_seq_titular,
		pls_obter_dados_segurado(d.nr_segurado, 'PF') cd_pessoa_fisica
	from	pls_segurado		b,
		pls_contrato_temp	a,
		pls_sca_vinculo		c,
		pls_tabela_preco	d,
		pls_plano		e
	where	a.nr_seq_contrato	= b.nr_seq_contrato
	and	c.nr_seq_segurado	= b.nr_sequencia
	and	c.nr_seq_tabela		= d.nr_sequencia
	and	c.nr_seq_plano		= e.nr_sequencia
	and	e.ie_permite_reajuste	= 'S'
	and	a.nr_seq_contrato	= nr_seq_contrato_pc
	and	c.nr_mes_reajuste	= nr_mes_reajuste_pc
	and	dt_reajuste_pc		>= c.dt_inicio_vigencia
	and	not exists (	SELECT	1
				from	pls_sca_regra_contrato x
				where	x.nr_seq_contrato	= a.nr_seq_contrato
				and	x.nr_seq_tabela		= c.nr_seq_tabela);

BEGIN

for r_c01_w in c01(	nr_seq_contrato_p,
			(to_char(current_setting('pls_reajuste_coletivo_pck.pls_reajuste_w')::pls_reajuste%rowtype.dt_reajuste,'mm'))::numeric ,
			fim_mes(dt_reajuste_p)) loop
	begin
	CALL pls_reajuste_coletivo_pck.alimentar_vetor_reaj_tabela(	r_c01_w.nr_seq_tabela, nr_seq_reajuste_p, r_c01_w.nr_seq_contrato,
					null, r_c01_w.nr_seq_plano_sca, r_c01_w.dt_contrato, 
					r_c01_w.nr_seq_segurado, r_c01_w.nr_seq_titular, r_c01_w.cd_pessoa_fisica);
	CALL CALL pls_reajuste_coletivo_pck.inserir_reajuste_tabela('N');
	end;
end loop; --C01

END;

$body$
