CREATE OR REPLACE PROCEDURE pls_reajuste_coletivo_pck.inserir_tabela_sca_regra_cont ( nr_seq_reajuste_p pls_reajuste.nr_sequencia%type, nr_seq_contrato_p pls_reajuste.nr_seq_contrato%type) AS $body$
DECLARE


C01 CURSOR(	nr_seq_contrato_pc	pls_reajuste.nr_seq_contrato%type,
		nr_mes_reajuste_pc	bigint) FOR
	SELECT	b.nr_sequencia nr_seq_tabela,
		c.nr_sequencia nr_seq_plano,
		a.nr_seq_contrato,
		a.dt_contrato,
		b.dt_inicio_vigencia,
		b.nr_segurado nr_seq_segurado,
		coalesce((pls_obter_dados_segurado(b.nr_segurado, 'NRTI'))::numeric , b.nr_segurado) nr_seq_titular,
		pls_obter_dados_segurado(b.nr_segurado, 'PF') cd_pessoa_fisica
	from	pls_contrato_temp	a,
		pls_tabela_preco	b,
		pls_plano		c,
		pls_sca_regra_contrato	d
	where	c.nr_sequencia		= b.nr_seq_plano
	and	c.nr_sequencia		= d.nr_seq_plano
	and	b.nr_sequencia		= d.nr_seq_tabela
	and	a.nr_seq_contrato	= d.nr_seq_contrato
	and	b.nr_contrato		= a.nr_seq_contrato
	and	c.ie_permite_reajuste	= 'S'
	and	a.nr_seq_contrato	= nr_seq_contrato_pc
	and	a.nr_mes_reajuste	= nr_mes_reajuste_pc;

BEGIN

for r_c01_w in c01(nr_seq_contrato_p, (to_char(current_setting('pls_reajuste_coletivo_pck.pls_reajuste_w')::pls_reajuste%rowtype.dt_reajuste,'mm'))::numeric ) loop
	begin
	CALL pls_reajuste_coletivo_pck.alimentar_vetor_reaj_tabela(	r_c01_w.nr_seq_tabela, nr_seq_reajuste_p, r_c01_w.nr_seq_contrato,
					null, r_c01_w.nr_seq_plano, r_c01_w.dt_contrato, 
					r_c01_w.nr_seq_segurado, r_c01_w.nr_seq_titular, r_c01_w.cd_pessoa_fisica);
	CALL CALL pls_reajuste_coletivo_pck.inserir_reajuste_tabela('N');
	end;
end loop; --C01

END;

