CREATE OR REPLACE PROCEDURE pls_reajuste_coletivo_pck.liberar_reajuste ( nr_seq_reajuste_p pls_reajuste.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
DECLARE


dt_preco_reajuste_w	timestamp;

C01 CURSOR(	nr_seq_reajuste_pc	pls_reajuste.nr_sequencia%type)	FOR
	SELECT	nr_sequencia nr_seq_reajuste,
		ie_reajustar_copartic,
		ie_reajustar_inscricao,
		dt_reajuste,
		dt_aplicacao_reajuste,
		ie_reajustar_via_adic,
		ie_reajustar_vl_manutencao,
		tx_reajuste
	from	pls_reajuste
	where	nr_seq_lote_referencia = nr_seq_reajuste_pc;

BEGIN

CALL CALL CALL pls_reajuste_coletivo_pck.carregar_filtros_lote(nr_seq_reajuste_p);
CALL CALL CALL CALL CALL pls_reajuste_coletivo_pck.carregar_parametros(cd_estabelecimento_p);

for r_c01_w in c01(nr_seq_reajuste_p) loop
	begin
	if (current_setting('pls_reajuste_coletivo_pck.ie_data_preco_reajuste_w')::pls_parametros.ie_data_preco_reajuste%type = 'A') then
		dt_preco_reajuste_w	:= coalesce(r_c01_w.dt_aplicacao_reajuste, r_c01_w.dt_reajuste);
	else
		dt_preco_reajuste_w	:= r_c01_w.dt_reajuste;
	end if;
	
	pls_reajuste_pck.liberar_reajustes(r_c01_w.nr_seq_reajuste, nm_usuario_p);
	
	aplicar_reajuste_apropriacao(r_c01_w.nr_seq_reajuste, r_c01_w.tx_reajuste, nm_usuario_p);
	
	if (r_c01_w.ie_reajustar_copartic = 'S') then
		pls_reajuste_pck.aplicar_reajuste_copartic(r_c01_w.nr_seq_reajuste, dt_preco_reajuste_w, nm_usuario_p);
	end if;
	
	if (r_c01_w.ie_reajustar_inscricao = 'S') then
		pls_reajuste_pck.aplicar_reajuste_taxa_insc(r_c01_w.nr_seq_reajuste, dt_preco_reajuste_w, nm_usuario_p);
	end if;
	
	if (r_c01_w.ie_reajustar_via_adic = 'S') then
		pls_reajuste_pck.aplicar_reajuste_via_adic(	r_c01_w.nr_seq_reajuste, dt_preco_reajuste_w, nm_usuario_p,
								cd_estabelecimento_p);
	end if;
	
	if (r_c01_w.ie_reajustar_vl_manutencao = 'S') then
		pls_reajuste_pck.aplicar_reajuste_pos_estab(	r_c01_w.nr_seq_reajuste, dt_preco_reajuste_w, nm_usuario_p,
								cd_estabelecimento_p);
	end if;
	
	CALL pls_reajuste_pck.aplicar_reajuste_benef(r_c01_w.nr_seq_reajuste, dt_preco_reajuste_w, current_setting('pls_reajuste_coletivo_pck.ie_reajustar_benef_cancelado_w')::pls_parametros.ie_reajustar_benef_cancelado%type,
						nm_usuario_p, cd_estabelecimento_p);
	
	pls_reajuste_pck.aplicar_reajuste_benef_sca(	r_c01_w.nr_seq_reajuste, dt_preco_reajuste_w, current_setting('pls_reajuste_coletivo_pck.ie_reajustar_benef_cancelado_w')::pls_parametros.ie_reajustar_benef_cancelado%type,
							current_setting('pls_reajuste_coletivo_pck.ie_mes_cobranca_reaj_w')::pls_parametros.ie_mes_cobranca_reaj%type, current_setting('pls_reajuste_coletivo_pck.ie_mes_cobranca_reaj_regulam_w')::pls_parametros.ie_mes_cobranca_reaj_reg%type, nm_usuario_p,
							cd_estabelecimento_p);
	end;
end loop; --C01

update	pls_reajuste
set	ie_status		= '2',
	dt_liberacao		= clock_timestamp(),
	nm_usuario_liberacao	= nm_usuario_p,
	nm_usuario		= nm_usuario_p,
	dt_atualizacao		= clock_timestamp()
where	nr_sequencia		= nr_seq_reajuste_p;

update	pls_reajuste
set	ie_status		= '2',
	dt_liberacao		= clock_timestamp(),
	nm_usuario_liberacao	= nm_usuario_p,
	nm_usuario		= nm_usuario_p,
	dt_atualizacao		= clock_timestamp()
where	nr_seq_lote_referencia	= nr_seq_reajuste_p;

commit;

END;

$body$
