CREATE OR REPLACE PROCEDURE pls_reajuste_coletivo_pck.verificar_processo_reajuste (nr_seq_reajuste_p pls_reajuste.nr_sequencia%type) AS $body$
DECLARE


qt_processo_reaj_w		bigint;


BEGIN

-- O lote de deflator que for criado fica vinculado ao lote de reajuste origem.


select	count(1)
into STRICT	qt_processo_reaj_w
from	pls_reajuste_tabela a,
	pls_reajuste b
where	b.nr_sequencia = a.nr_seq_reajuste
and	b.nr_sequencia = nr_seq_reajuste_p
and	(a.nr_seq_processo IS NOT NULL AND a.nr_seq_processo::text <> '');

if (qt_processo_reaj_w > 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(1029738);
	--Ha tabelas de preco reajustadas com processo judicial. E necessario aplicar o percentual de correcao com 0%.

end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
