CREATE OR REPLACE PROCEDURE pls_reajuste_individual_pck.inserir_lote_copartic_desf () AS $body$
BEGIN
if (tb_nr_seq_contrato_desf_w.count > 0) then
	forall i in tb_nr_seq_contrato_desf_w.first..tb_nr_seq_contrato_desf_w.last
		insert into pls_lote_reaj_copartic(nr_sequencia, dt_atualizacao, nm_usuario,
			dt_atualizacao_nrec, nm_usuario_nrec, cd_estabelecimento,
			dt_referencia, tx_reajuste, tx_reajuste_vl_maximo, 
			nr_seq_reajuste, ie_vinculo_coparticipacao, nr_seq_contrato,
			nr_seq_intercambio)
		values (nextval('pls_lote_reaj_copartic_seq'), clock_timestamp(), nm_usuario_p,
			clock_timestamp(), nm_usuario_p, cd_estabelecimento_p, 
			current_setting('pls_reajuste_individual_pck.pls_reajuste_w')::pls_reajuste%rowtype.dt_reajuste, tb_tx_reajuste_desf_w(i), tb_tx_reajuste_vl_max_w(i), 
			nr_seq_reajuste_desf_p, tb_ie_vinculo_coparticipacao_w(i), tb_nr_seq_contrato_desf_w(i),
			tb_nr_seq_intercambio_desf_w(i));
	commit;
end if;

