SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_reajuste_individual_pck.inserir_tab_sca_regra_contr ( ie_tipo_operacao_plano_p text) AS $body$
DECLARE


C01 CURSOR(	nr_mes_reajuste_pc		pls_contrato.nr_mes_reajuste%type,
		ie_tipo_operacao_plano_pc	text,
		ie_regulamentacao_pc		pls_reajuste.ie_regulamentacao%type) FOR
	--Buscar as tabelas de preco do SCA vinculadas a regra do contrato

	SELECT	b.nr_sequencia nr_seq_tabela,
		c.nr_sequencia nr_seq_plano_sca,
		a.dt_contrato,
		a.nr_seq_contrato,
		b.nr_segurado nr_seq_segurado,
		a.dt_rescisao_contrato,
		coalesce((pls_obter_dados_segurado(b.nr_segurado, 'NRTI'))::numeric , b.nr_segurado) nr_seq_titular,
		pls_obter_dados_segurado(b.nr_segurado, 'PF') cd_pessoa_fisica,
		'C' ie_reajuste,
		coalesce(	(SELECT	max(x.qt_intervalo)
			from	pls_contrato x
			where	x.nr_sequencia = a.nr_seq_contrato),12) qt_intervalo,
		(select	max(x.nr_seq_grupo_reajuste)
		from	pls_contrato x
		where	x.nr_sequencia = a.nr_seq_contrato) nr_seq_grupo_reajuste
	from	pls_contrato_temp	a,
		pls_tabela_preco	b,
		pls_plano		c,
		pls_sca_regra_contrato	d
	where	c.nr_sequencia		= b.nr_seq_plano
	and	c.nr_sequencia		= d.nr_seq_plano
	and	b.nr_sequencia		= d.nr_seq_tabela
	and	a.nr_seq_contrato	= d.nr_seq_contrato
	and	b.nr_contrato		= a.nr_seq_contrato
	and	a.nr_mes_reajuste	= nr_mes_reajuste_pc
	and	c.ie_permite_reajuste	= 'S'
	and	((c.ie_tipo_operacao = ie_tipo_operacao_plano_pc) or (coalesce(ie_tipo_operacao_plano_pc::text, '') = ''))
	and	((c.ie_regulamentacao = ie_regulamentacao_pc) or (coalesce(ie_regulamentacao_pc::text, '') = ''));

BEGIN

for r_c01_w in c01(	(to_char(current_setting('pls_reajuste_individual_pck.pls_reajuste_w')::pls_reajuste%rowtype.dt_reajuste,'mm'))::numeric ,
			ie_tipo_operacao_plano_p,
			current_setting('pls_reajuste_individual_pck.pls_reajuste_w')::pls_reajuste%rowtype.ie_regulamentacao) loop
	begin

	alimentar_vetor(r_c01_w.nr_seq_tabela,
			r_c01_w.nr_seq_plano_sca,
			r_c01_w.dt_contrato,
			r_c01_w.nr_seq_contrato,
			r_c01_w.nr_seq_segurado,
			r_c01_w.dt_rescisao_contrato,
			r_c01_w.nr_seq_titular,
			r_c01_w.cd_pessoa_fisica,
			r_c01_w.ie_reajuste,
			r_c01_w.qt_intervalo,
			null,
			r_c01_w.nr_seq_grupo_reajuste);
	
	CALL pls_reajuste_individual_pck.inserir_tabelas('N');
	end;
end loop; --C01

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
