SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_reajuste_individual_pck.inserir_tab_sca_vinculo ( ie_tipo_operacao_plano_p text) AS $body$
DECLARE


C01 CURSOR(	nr_mes_reajuste_pc		pls_contrato.nr_mes_reajuste%type,
		dt_reajuste_pc			pls_reajuste.dt_reajuste%type,
		ie_tipo_operacao_plano_pc	text,
		ie_regulamentacao_pc		pls_reajuste.ie_regulamentacao%type) FOR
	SELECT	d.nr_sequencia nr_seq_tabela,
		e.nr_sequencia nr_seq_plano_sca,
		a.dt_contrato,
		a.nr_seq_contrato,
		d.nr_segurado nr_seq_segurado,
		b.dt_rescisao,
		coalesce((pls_obter_dados_segurado(d.nr_segurado, 'NRTI'))::numeric , d.nr_segurado) nr_seq_titular,
		pls_obter_dados_segurado(d.nr_segurado, 'PF') cd_pessoa_fisica,
		(SELECT	max(x.ie_reajuste)
		from	pls_contrato x
		where	x.nr_sequencia = a.nr_seq_contrato) ie_reajuste,
		coalesce(	(select	max(x.qt_intervalo)
			from	pls_contrato x
			where	x.nr_sequencia = a.nr_seq_contrato),12) qt_intervalo,
		b.dt_contratacao,
		(select	max(x.nr_seq_grupo_reajuste)
		from	pls_contrato x
		where	x.nr_sequencia = a.nr_seq_contrato) nr_seq_grupo_reajuste
	from	pls_segurado		b,
		pls_contrato_temp	a,
		pls_sca_vinculo		c,
		pls_tabela_preco	d,
		pls_plano		e,
		pls_plano		f
	where	a.nr_seq_contrato	= b.nr_seq_contrato
	and	c.nr_seq_segurado	= b.nr_sequencia
	and	c.nr_seq_tabela		= d.nr_sequencia
	and	c.nr_seq_plano		= e.nr_sequencia
	and	b.nr_seq_plano		= f.nr_sequencia
	and	c.nr_mes_reajuste	= nr_mes_reajuste_pc
	and	fim_mes(dt_reajuste_pc) >= c.dt_inicio_vigencia
	and	not exists (	select	1
				from	pls_sca_regra_contrato x
				where	x.nr_seq_contrato	= a.nr_seq_contrato
				and	x.nr_seq_tabela		= c.nr_seq_tabela)
	and	((e.ie_tipo_operacao = ie_tipo_operacao_plano_pc) or (coalesce(ie_tipo_operacao_plano_pc::text, '') = ''))
	and	((f.ie_regulamentacao = ie_regulamentacao_pc) or (coalesce(ie_regulamentacao_pc::text, '') = ''));

BEGIN

for r_c01_w in c01(	(to_char(current_setting('pls_reajuste_individual_pck.pls_reajuste_w')::pls_reajuste%rowtype.dt_reajuste,'mm'))::numeric ,
			current_setting('pls_reajuste_individual_pck.pls_reajuste_w')::pls_reajuste%rowtype.dt_reajuste,
			ie_tipo_operacao_plano_p,
			current_setting('pls_reajuste_individual_pck.pls_reajuste_w')::pls_reajuste%rowtype.ie_regulamentacao) loop
	begin

	alimentar_vetor(r_c01_w.nr_seq_tabela,
			r_c01_w.nr_seq_plano_sca,
			r_c01_w.dt_contrato,
			r_c01_w.nr_seq_contrato,
			r_c01_w.nr_seq_segurado,
			r_c01_w.dt_rescisao,
			r_c01_w.nr_seq_titular,
			r_c01_w.cd_pessoa_fisica,
			r_c01_w.ie_reajuste,
			r_c01_w.qt_intervalo,
			r_c01_w.dt_contratacao,
			r_c01_w.nr_seq_grupo_reajuste);
	
	CALL pls_reajuste_individual_pck.inserir_tabelas('N');
	end;
end loop; -- C01

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
