SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_reajuste_individual_pck.inserir_tabela_orig_contrato ( ie_tipo_operacao_plano_p text) AS $body$
DECLARE


C01 CURSOR(	nr_mes_reajuste_pc		bigint,
		ie_tipo_operacao_plano_pc	text,
		ie_regulamentacao_pc		pls_reajuste.ie_regulamentacao%type) FOR
	--Buscar as tabelas de preco vinculadas ao contrato, nesse select nao pode restringir pelo campo PLS_CONTRATO.IE_REAJUSTE, pois a tabela de comercializacao do contrato sempre deve ser reajustada no mes de reajuste do contrato

	SELECT	b.nr_sequencia nr_seq_tabela,
		c.nr_sequencia nr_seq_plano,
		a.nr_seq_contrato,
		a.dt_contrato,
		a.dt_rescisao_contrato,
		'C' ie_reajuste,
		coalesce(	(SELECT	max(x.qt_intervalo)
			from	pls_contrato x
			where	x.nr_sequencia = a.nr_seq_contrato),12) qt_intervalo,
		(select	max(x.nr_seq_grupo_reajuste)
		from	pls_contrato x
		where	x.nr_sequencia = a.nr_seq_contrato) nr_seq_grupo_reajuste
	from	pls_contrato_temp	a,
		pls_contrato_plano	d,
		pls_tabela_preco	b,
		pls_plano		c
	where	a.nr_seq_contrato	= d.nr_seq_contrato
	and	c.nr_sequencia		= b.nr_seq_plano
	and	b.nr_sequencia		= d.nr_seq_tabela
	and	c.ie_tipo_contratacao	= 'I'
	and	a.nr_mes_reajuste	= nr_mes_reajuste_pc
	and	((c.ie_tipo_operacao = ie_tipo_operacao_plano_pc) or (coalesce(ie_tipo_operacao_plano_pc::text, '') = ''))
	and	((c.ie_regulamentacao = ie_regulamentacao_pc) or (coalesce(ie_regulamentacao_pc::text, '') = ''))
	
union

	--Buscar as tabelas de preco que ja foram vinculadas ao contrato

	select	b.nr_sequencia nr_seq_tabela,
		c.nr_sequencia nr_seq_plano,
		a.nr_seq_contrato,
		a.dt_contrato,
		a.dt_rescisao_contrato,
		'C' ie_reajuste,
		coalesce(	(select	max(x.qt_intervalo)
			from	pls_contrato x
			where	x.nr_sequencia = a.nr_seq_contrato),12) qt_intervalo,
		(select	max(x.nr_seq_grupo_reajuste)
		from	pls_contrato x
		where	x.nr_sequencia = a.nr_seq_contrato) nr_seq_grupo_reajuste
	from	pls_contrato_temp	a,
		pls_tabela_preco	b,
		pls_plano		c
	where	a.nr_seq_contrato	= b.nr_contrato
	and	c.nr_sequencia		= b.nr_seq_plano
	and	c.ie_tipo_contratacao	= 'I'
	and	a.nr_mes_reajuste	= nr_mes_reajuste_pc
	and	coalesce(b.nr_segurado::text, '') = ''
	and	((c.ie_tipo_operacao = 'B') and (('A' <> ie_tipo_operacao_plano_pc) or (coalesce(ie_tipo_operacao_plano_pc::text, '') = '')))
	and	((c.ie_regulamentacao = ie_regulamentacao_pc) or (coalesce(ie_regulamentacao_pc::text, '') = ''));

BEGIN

for r_c01_w in c01(	(to_char(current_setting('pls_reajuste_individual_pck.pls_reajuste_w')::pls_reajuste%rowtype.dt_reajuste,'mm'))::numeric ,
			ie_tipo_operacao_plano_p,
			current_setting('pls_reajuste_individual_pck.pls_reajuste_w')::pls_reajuste%rowtype.ie_regulamentacao) loop
	begin
	
	alimentar_vetor(r_c01_w.nr_seq_tabela,
			r_c01_w.nr_seq_plano,
			r_c01_w.dt_contrato,
			r_c01_w.nr_seq_contrato,
			null,
			r_c01_w.dt_rescisao_contrato,
			null,
			null,
			r_c01_w.ie_reajuste,
			r_c01_w.qt_intervalo,
			null,
			r_c01_w.nr_seq_grupo_reajuste);
	
	CALL pls_reajuste_individual_pck.inserir_tabelas('N');
	end;
end loop; --C01

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_reajuste_individual_pck.inserir_tabela_orig_contrato ( ie_tipo_operacao_plano_p text) FROM PUBLIC;
