CREATE OR REPLACE PROCEDURE pls_reajuste_individual_pck.inserir_tabelas (ie_descarregar_vetores_p text) AS $body$
BEGIN

if	((nr_indice_reaj_tab_w >= pls_util_pck.qt_registro_transacao_w) or (ie_descarregar_vetores_p = 'S')) then
	if (tb_nr_seq_tabela_w.count > 0) then
		forall i in tb_nr_seq_tabela_w.first..tb_nr_seq_tabela_w.last
			insert into pls_reajuste_tabela(nr_sequencia, dt_atualizacao, nm_usuario,
					dt_atualizacao_nrec, nm_usuario_nrec, nr_seq_reajuste,
					nr_seq_tabela, nr_seq_plano, dt_contrato,
					dt_inicio_vigencia, nr_seq_contrato, ie_origem_tabela,
					nr_seq_processo, tx_reajuste)
				values (nextval('pls_reajuste_tabela_seq'), clock_timestamp(), nm_usuario_p,
					clock_timestamp(), nm_usuario_p, nr_seq_reajuste_p,
					tb_nr_seq_tabela_w(i), tb_nr_seq_plano_w(i), tb_dt_contrato_w(i),
					tb_dt_reajute_w(i), tb_nr_seq_contrato_w(i), tb_ie_origem_tabela_w(i),
					tb_nr_seq_processo_w(i), tb_tx_reajuste_w(i));
		commit;
	end if;
	
	CALL limpar_vetor_reaj_tabela();
end if;

END;

$body$
