CREATE OR REPLACE FUNCTION pls_reajuste_individual_pck.verifica_grupo_produto (nr_seq_grupo_produto_p pls_grupo_produto.nr_sequencia%type, nr_seq_plano_p pls_plano.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


qt_regra_grupo_prod_w		bigint;


BEGIN

select	count(1)
into STRICT	qt_regra_grupo_prod_w
from	pls_produto_grupo
where	nr_seq_grupo = nr_seq_grupo_produto_p
and	nr_seq_plano = nr_seq_plano_p;

if (qt_regra_grupo_prod_w = 0) then
	return 'N';
else
	return 'S';
end if;

END;

