CREATE OR REPLACE PROCEDURE pls_reajuste_pck.atualizar_reajuste_tabela () AS $body$
BEGIN

if (tb_nr_seq_reaj_tabela_w.count > 0) then
	forall i in tb_nr_seq_reaj_tabela_w.first..tb_nr_seq_reaj_tabela_w.last
		update	pls_reajuste_tabela
		set	dt_liberacao	= clock_timestamp(),
			dt_atualizacao	= clock_timestamp(),
			nm_usuario	= nm_usuario_p
		where	nr_sequencia	= tb_nr_seq_reaj_tabela_w(i);
	commit;
end if;

CALL pls_reajuste_pck.limpar_vetor_att_reaj_tabela();

END;

$body$
