SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_reajuste_pck.atualizar_reajustes () AS $body$
BEGIN

if (tb_nr_seq_reaj_preco_w.count > 0) then
	forall i in tb_nr_seq_reaj_preco_w.first..tb_nr_seq_reaj_preco_w.last
		update	pls_reajuste_preco
		set	dt_liberacao	= clock_timestamp(),
			nm_usuario_lib	= nm_usuario_p
		where	nr_sequencia	= tb_nr_seq_reaj_preco_w(i);
	commit;
end if;

if (tb_nr_seq_plano_preco_w.count > 0) then
	forall i in tb_nr_seq_plano_preco_w.first..tb_nr_seq_plano_preco_w.last
		update	pls_plano_preco
		set	vl_preco_atual		= tb_vl_preco_atual_w(i),
			vl_preco_nao_subsid_atual = tb_vl_preco_nao_subsid_w(i),
			vl_minimo		= tb_vl_minimo_w(i),
			vl_adaptacao		= tb_vl_adaptacao_w(i),
			dt_atualizacao		= clock_timestamp(),
			nm_usuario		= nm_usuario_p
		where	nr_sequencia		= tb_nr_seq_plano_preco_w(i);
	commit;
end if;

CALL pls_reajuste_pck.limpar_vetor_att_reajustes();

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
