CREATE OR REPLACE PROCEDURE pls_reajuste_pck.inserir_incosistencia ( ie_descarregar_p text) AS $body$
BEGIN

if	((nr_indice_consistencia_w >= pls_util_pck.qt_registro_transacao_w) or (ie_descarregar_p = 'S')) then
	if (tb_nr_seq_tabela_consist_w.count > 0) then
		forall i in tb_nr_seq_tabela_consist_w.first..tb_nr_seq_tabela_consist_w.last
			insert	into	pls_reajuste_consistencia(	nr_sequencia, nr_seq_reajuste, nr_seq_consistencia,
					dt_atualizacao, nm_usuario, dt_atualizacao_nrec,
					nm_usuario_nrec, nr_seq_tabela)
				values (	nextval('pls_reajuste_consistencia_seq'), tb_nr_seq_reajuste_consist_w(i), tb_nr_seq_consistencia_w(i),
					clock_timestamp(), nm_usuario_p, clock_timestamp(),
					nm_usuario_p, tb_nr_seq_tabela_consist_w(i));
		commit;
	end if;
	
	CALL pls_reajuste_pck.limpar_vetor_tabela_consist();
else
	nr_indice_consistencia_w	:= nr_indice_consistencia_w + 1;
end if;

END;

$body$
