CREATE OR REPLACE PROCEDURE pls_reajuste_pck.inserir_lote_reaj_pos_estab ( ie_descarregar_p text) AS $body$
BEGIN

if	((nr_indice_reaj_pos_estab_w >= pls_util_pck.qt_registro_transacao_w) or (ie_descarregar_p = 'S')) then
	if (tb_nr_seq_reajuste_pos_estab_w.count > 0) then
		forall i in tb_nr_seq_reajuste_pos_estab_w.first..tb_nr_seq_reajuste_pos_estab_w.last
			insert into 	pls_lote_reaj_pos_estab(	nr_sequencia, dt_atualizacao, dt_atualizacao_nrec,
					nm_usuario, nm_usuario_nrec, dt_referencia,
					cd_estabelecimento, nr_seq_contrato, nr_seq_intercambio,
					nr_seq_reajuste, tx_reajuste)
			values (	nextval('pls_lote_reaj_pos_estab_seq'), clock_timestamp(), clock_timestamp(),
					nm_usuario_p, nm_usuario_p, current_setting('pls_reajuste_pck.pls_reajuste_w')::pls_reajuste%rowtype.dt_reajuste,
					cd_estabelecimento_p, tb_nr_seq_contrato_pos_estab_w(i), tb_nr_seq_interc_pos_estab_w(i),
					tb_nr_seq_reajuste_pos_estab_w(i), current_setting('pls_reajuste_pck.pls_reajuste_w')::pls_reajuste%rowtype.tx_reajuste);
		commit;
	end if;
	
	CALL pls_reajuste_pck.limpar_vetor_pos_estabelecido();
else
	nr_indice_reaj_pos_estab_w	:= nr_indice_reaj_pos_estab_w + 1;
end if;

END;

