CREATE OR REPLACE PROCEDURE pls_reajuste_pck.inserir_reajuste_copartic (ie_descarregar_p text) AS $body$
BEGIN

if	((nr_indice_reajuste_copartic_w >= pls_util_pck.qt_registro_transacao_w) or (ie_descarregar_p = 'S')) then
	if (tb_nr_seq_lote_reaj_copartic_w.count > 0) then
		forall i in tb_nr_seq_lote_reaj_copartic_w.first..tb_nr_seq_lote_reaj_copartic_w.last
			insert	into	pls_reajuste_copartic(	nr_sequencia, nr_seq_lote, cd_estabelecimento,
					nr_seq_regra_copartic, dt_atualizacao, nm_usuario,
					dt_atualizacao_nrec, nm_usuario_nrec, tx_reajuste,
					tx_valor_maximo, ie_origem_coparticipacao, nr_seq_regra_atual,
					nr_seq_copartic_ant, vl_base, vl_reajustado,
					vl_maximo_base, vl_maximo_reajustado)
			values (	nextval('pls_reajuste_copartic_seq'), tb_nr_seq_lote_reaj_copartic_w(i), cd_estabelecimento_p,
					tb_nr_seq_tipo_copartic_w(i), clock_timestamp(), nm_usuario_p,
					clock_timestamp(), nm_usuario_p, tb_tx_reajuste_w(i),
					tb_tx_reajuste_vl_maximo_w(i), tb_ie_vinculo_coparticipacao_w(i), tb_nr_seq_regra_coparticip_w(i),
					tb_nr_seq_regra_copartic_w(i), tb_vl_base_w(i), tb_vl_reajustado_w(i),
					tb_vl_maximo_w(i), tb_vl_maximo_reajustado_w(i));
		commit;
	end if;
	
	CALL pls_reajuste_pck.limpar_vetor_reaj_copartic();
else
	nr_indice_reajuste_copartic_w	:= nr_indice_reajuste_copartic_w + 1;
end if;

END;

$body$
