CREATE OR REPLACE PROCEDURE pls_reajuste_pck.inserir_reajuste_pos_estab ( ie_descarregar_p text) AS $body$
BEGIN

if	((nr_indice_reajuste_pos_estab_w >= pls_util_pck.qt_registro_transacao_w) or (ie_descarregar_p = 'S')) then
	if (tb_nr_seq_lote_pos_estab_w.count > 0) then
		forall i in tb_nr_seq_lote_pos_estab_w.first..tb_nr_seq_lote_pos_estab_w.last
			insert into	pls_reajuste_pos_estab(	nr_sequencia, dt_atualizacao, dt_atualizacao_nrec,
					nm_usuario, nm_usuario_nrec, ie_origem_regra,
					nr_seq_lote, nr_seq_regra_ant, vl_base, 
					vl_reajustado)
			values (	nextval('pls_reajuste_pos_estab_seq'), clock_timestamp(), clock_timestamp(),
					nm_usuario_p, nm_usuario_p, 'C', 
					tb_nr_seq_lote_pos_estab_w(i), tb_nr_seq_regra_ant_pos_est_w(i), tb_vl_base_pos_estab_w(i), 
					tb_vl_reajustado_pos_estab_w(i));
		commit;
	end if;
	
	CALL pls_reajuste_pck.limpar_vetor_pos_estab();
else
	nr_indice_reajuste_pos_estab_w	:= nr_indice_reajuste_pos_estab_w + 1;
end if;

END;

$body$
