CREATE OR REPLACE PROCEDURE pls_reajuste_pck.inserir_reajuste_preco () AS $body$
BEGIN
if (tb_nr_seq_plano_preco_w.count > 0) then
	forall i in tb_nr_seq_plano_preco_w.first..tb_nr_seq_plano_preco_w.last
		insert into	pls_reajuste_preco(nr_sequencia, dt_atualizacao, nm_usuario,
			dt_atualizacao_nrec, nm_usuario_nrec, dt_reajuste,
			nr_seq_preco, nr_seq_reajuste, vl_base,
			pr_reajustado, vl_reajustado, nr_seq_tabela,
			dt_periodo_inicial, dt_periodo_final, dt_autorizacao_ans,
			vl_preco_nao_subsidiado, vl_preco_nao_subsid_base, vl_minimo,
			vl_minimo_base, vl_adaptacao, vl_adaptacao_base)
		values (nextval('pls_reajuste_preco_seq'), clock_timestamp(), nm_usuario_p,
			clock_timestamp(), nm_usuario_p, tb_dt_reajuste_w(i),
			tb_nr_seq_plano_preco_w(i), nr_seq_reajuste_p, tb_vl_base_w(i),
			tb_pr_reajuste_w(i), tb_vl_base_w(i) + tb_vl_reajuste_w(i), tb_nr_seq_reaj_tabela_w(i),
			dt_periodo_inicial_w, dt_periodo_final_w, current_setting('pls_reajuste_pck.pls_reajuste_w')::pls_reajuste%rowtype.dt_autorizacao_ans,
			tb_vl_reajuste_nao_subsid_w(i) + tb_vl_reaj_nao_subsid_base_w(i), tb_vl_reaj_nao_subsid_base_w(i), tb_vl_minimo_w(i) + tb_vl_minimo_base_w(i),
			tb_vl_minimo_base_w(i), tb_vl_adaptacao_w(i) + tb_vl_adaptacao_base_w(i), tb_vl_adaptacao_base_w(i));
	commit;
end if;

CALL pls_reajuste_pck.limpar_vetor_reaj_preco();

END;

