CREATE OR REPLACE PROCEDURE pls_reajuste_pck.inserir_reajuste_tx_inscricao ( ie_descarregar_p text) AS $body$
BEGIN

if	((nr_indice_reajuste_tx_insc_w >= pls_util_pck.qt_registro_transacao_w) or (ie_descarregar_p = 'S')) then
	if (tb_nr_seq_lote_inscricao_w.count > 0) then
		forall i in tb_nr_seq_lote_inscricao_w.first..tb_nr_seq_lote_inscricao_w.last
			insert	into	pls_reajuste_inscricao(	nr_sequencia, nr_seq_lote, cd_estabelecimento,
					nr_seq_regra_inscricao, dt_atualizacao, nm_usuario,
					dt_atualizacao_nrec, nm_usuario_nrec, tx_reajuste,
					ie_origem_inscricao, vl_base, vl_reajustado)
				values (	nextval('pls_reajuste_inscricao_seq'), tb_nr_seq_lote_inscricao_w(i), cd_estabelecimento_p,
					current_setting('pls_reajuste_pck.tb_nr_seq_regra_inscricao_w')::pls_util_cta_pck.t_number_table(i), clock_timestamp(), nm_usuario_p,
					clock_timestamp(), nm_usuario_p, tb_tx_reajuste_tx_insc_w(i),
					tb_ie_origem_inscricao_w(i), tb_vl_base_tx_insc_w(i), tb_vl_base_reaj_tx_insc_w(i));
		commit;
	end if;
	
	CALL pls_reajuste_pck.limpar_vetor_tx_inscricao();
else
	nr_indice_reajuste_tx_insc_w	:= nr_indice_reajuste_tx_insc_w + 1;
end if;
END;

$body$
