CREATE OR REPLACE PROCEDURE pls_reajuste_pck.inserir_reajuste_via_adicional ( ie_descarregar_p text) AS $body$
BEGIN

if	((nr_indice_reajuste_via_adic_w >= pls_util_pck.qt_registro_transacao_w) or (ie_descarregar_p = 'S')) then
	if (tb_nr_seq_lote_via_adic_w.count > 0) then
		forall i in tb_nr_seq_lote_via_adic_w.first..tb_nr_seq_lote_via_adic_w.last
			insert into	pls_reajuste_via_cart(	nr_sequencia, dt_atualizacao, dt_atualizacao_nrec,
					nm_usuario, nm_usuario_nrec, nr_seq_lote,
					vl_base, vl_reajustado, nr_seq_regra_ant,
					ie_origem_regra)
			values (	nextval('pls_reajuste_via_cart_seq'), clock_timestamp(), clock_timestamp(),
					nm_usuario_p, nm_usuario_p, tb_nr_seq_lote_via_adic_w(i),
					tb_vl_base_via_adic_w(i), tb_vl_reajustado_via_adic_w(i), tb_nr_seq_regra_ant_via_adic_w(i),
					tb_ie_origem_regra_w(i));
		commit;
	end if;
	
	CALL pls_reajuste_pck.limpar_vetor_via_adicional();
else
	nr_indice_reajuste_via_adic_w := nr_indice_reajuste_via_adic_w + 1;
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
