CREATE OR REPLACE PROCEDURE pls_reajuste_pck.inserir_regra_pos_estabelecido () AS $body$
BEGIN

if (tb_nr_seq_regra_pos_estab_w.count > 0) then
	forall i in tb_nr_seq_regra_pos_estab_w.first..tb_nr_seq_regra_pos_estab_w.last
		insert into	pls_regra_pos_estabelecido(	nr_sequencia, tx_administracao, vl_informado,
				dt_atualizacao, nm_usuario, dt_atualizacao_nrec,
				nm_usuario_nrec, dt_vigencia_inicio, nr_seq_contrato,
				ie_cobranca, ie_autorizacao_previa, nr_seq_intercambio,
				nr_seq_grupo_servico, nr_seq_grupo_material, ie_repassa_medico)
		values (	nextval('pls_regra_pos_estabelecido_seq'), tb_tx_administracao_w(i), tb_vl_reajustado_w(i),
				clock_timestamp(), nm_usuario_p, clock_timestamp(),
				nm_usuario_p, dt_reajuste_p, tb_nr_seq_contrato_w(i),
				tb_ie_cobranca_w(i), tb_ie_autorizacao_previa_w(i), current_setting('pls_reajuste_pck.tb_nr_seq_intercambio_w')::pls_util_cta_pck.t_number_table(i),
				tb_nr_seq_grupo_servico_w(i), tb_nr_seq_grupo_material_w(i), tb_ie_repassa_medico_w(i))
		RETURNING nr_sequencia
		BULK COLLECT INTO tb_nr_seq_regra_pos_estab_wr;
	commit;
	
	forall i in tb_nr_seq_regra_pos_estab_wr.first..tb_nr_seq_regra_pos_estab_wr.last
		update	pls_reajuste_pos_estab
		set	nr_seq_regra_reajustada	= tb_nr_seq_regra_pos_estab_wr(i)
		where	nr_sequencia		= tb_nr_seq_reaj_pos_estab_w(i);
	commit;
	
	forall i in tb_nr_seq_regra_pos_estab_w.first..tb_nr_seq_regra_pos_estab_w.last
		update	pls_regra_pos_estabelecido
		set	dt_vigencia_fim = fim_dia(dt_reajuste_p-1)
		where	nr_sequencia = tb_nr_seq_regra_pos_estab_w(i);
	commit;
end if;

CALL pls_reajuste_pck.limpar_vetor_inserir_pos_estab();

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
